import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    IconButton,
    Tooltip,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';
import {
    Add as AddIcon,
    Edit as EditIcon,
    Delete as DeleteIcon,
} from '@mui/icons-material';
import axios from 'axios';

const CarManagement = () => {
    const [cars, setCars] = useState([]);
    const [teams, setTeams] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedCar, setSelectedCar] = useState(null);
    const [formData, setFormData] = useState({
        carType: '',
        bks: '',
        team: '',
        yearOfManufacture: '',
        registrationPeriod: '',
        registrationName: '',
        valuation: '',
        insurancePeriodTNDS: '',
        insuranceSellerTNDS: '',
        insurancePeriodBHVC: '',
        insuranceSellerBHVC: '',
        insurancePeriodGPS: '',
        descriptionGPS: '',
        insurancePeriod4G: '',
        insuranceSeller4G: '',
        description: '',
    });

    useEffect(() => {
        fetchCars();
        fetchTeams();
    }, []);

    const fetchCars = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/car');
            setCars(response.data);
        } catch (error) {
            console.error('Error fetching cars:', error);
        }
    };

    const fetchTeams = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/team');
            setTeams(response.data);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    const handleOpenDialog = (car = null) => {
        if (car) {
            setSelectedCar(car);
            setFormData({
                carType: car.carType,
                bks: car.bks,
                team: car.team,
                yearOfManufacture: car.yearOfManufacture,
                registrationPeriod: car.registrationPeriod,
                registrationName: car.registrationName,
                valuation: car.valuation,
                insurancePeriodTNDS: car.insurancePeriodTNDS,
                insuranceSellerTNDS: car.insuranceSellerTNDS,
                insurancePeriodBHVC: car.insurancePeriodBHVC,
                insuranceSellerBHVC: car.insuranceSellerBHVC,
                insurancePeriodGPS: car.insurancePeriodGPS,
                descriptionGPS: car.descriptionGPS,
                insurancePeriod4G: car.insurancePeriod4G,
                insuranceSeller4G: car.insuranceSeller4G,
                description: car.description,
            });
        } else {
            setSelectedCar(null);
            setFormData({
                carType: '',
                bks: '',
                team: '',
                yearOfManufacture: '',
                registrationPeriod: '',
                registrationName: '',
                valuation: '',
                insurancePeriodTNDS: '',
                insuranceSellerTNDS: '',
                insurancePeriodBHVC: '',
                insuranceSellerBHVC: '',
                insurancePeriodGPS: '',
                descriptionGPS: '',
                insurancePeriod4G: '',
                insuranceSeller4G: '',
                description: '',
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedCar(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            if (selectedCar) {
                await axios.put(`http://localhost:3076/api/vanminh/car/${selectedCar._id}`, formData);
            } else {
                await axios.post('http://localhost:3076/api/vanminh/car', formData);
            }
            handleCloseDialog();
            fetchCars();
        } catch (error) {
            console.error('Error saving car:', error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa?')) {
            try {
                await axios.delete(`http://localhost:3076/api/vanminh/car/${id}`);
                fetchCars();
            } catch (error) {
                console.error('Error deleting car:', error);
            }
        }
    };

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý xe</Typography>
                <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => handleOpenDialog()}
                >
                    Thêm mới
                </Button>
            </Box>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Loại xe</TableCell>
                            <TableCell>Biển số</TableCell>
                            <TableCell>Team</TableCell>
                            <TableCell>Năm sản xuất</TableCell>
                            <TableCell>Thao tác</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {cars.map((car) => (
                            <TableRow key={car._id}>
                                <TableCell>{car.carType}</TableCell>
                                <TableCell>{car.bks}</TableCell>
                                <TableCell>
                                    {teams.find(t => t._id === car.team)?.label}
                                </TableCell>
                                <TableCell>{car.yearOfManufacture}</TableCell>
                                <TableCell>
                                    <Tooltip title="Sửa">
                                        <IconButton onClick={() => handleOpenDialog(car)}>
                                            <EditIcon />
                                        </IconButton>
                                    </Tooltip>
                                    <Tooltip title="Xóa">
                                        <IconButton onClick={() => handleDelete(car._id)}>
                                            <DeleteIcon />
                                        </IconButton>
                                    </Tooltip>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                <DialogTitle>
                    {selectedCar ? 'Cập nhật thông tin xe' : 'Thêm mới xe'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Loại xe"
                            name="carType"
                            value={formData.carType}
                            onChange={handleInputChange}
                            fullWidth
                            required
                        />
                        <TextField
                            label="Biển số"
                            name="bks"
                            value={formData.bks}
                            onChange={handleInputChange}
                            fullWidth
                            required
                        />
                        <FormControl fullWidth>
                            <InputLabel>Team</InputLabel>
                            <Select
                                name="team"
                                value={formData.team}
                                onChange={handleInputChange}
                                label="Team"
                                required
                            >
                                {teams.map((team) => (
                                    <MenuItem key={team._id} value={team._id}>
                                        {team.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <TextField
                            label="Năm sản xuất"
                            name="yearOfManufacture"
                            value={formData.yearOfManufacture}
                            onChange={handleInputChange}
                            fullWidth
                            type="date"
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Thời hạn đăng kiểm"
                            name="registrationPeriod"
                            value={formData.registrationPeriod}
                            onChange={handleInputChange}
                            fullWidth
                            type="date"
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Tên đăng ký"
                            name="registrationName"
                            value={formData.registrationName}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Định giá"
                            name="valuation"
                            value={formData.valuation}
                            onChange={handleInputChange}
                            fullWidth
                            type="number"
                        />
                        <TextField
                            label="Thời hạn bảo hiểm TNDS"
                            name="insurancePeriodTNDS"
                            value={formData.insurancePeriodTNDS}
                            onChange={handleInputChange}
                            fullWidth
                            type="date"
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Đơn vị bán bảo hiểm TNDS"
                            name="insuranceSellerTNDS"
                            value={formData.insuranceSellerTNDS}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Thời hạn bảo hiểm BHVC"
                            name="insurancePeriodBHVC"
                            value={formData.insurancePeriodBHVC}
                            onChange={handleInputChange}
                            fullWidth
                            type="date"
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Đơn vị bán bảo hiểm BHVC"
                            name="insuranceSellerBHVC"
                            value={formData.insuranceSellerBHVC}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Thời hạn bảo hiểm GPS"
                            name="insurancePeriodGPS"
                            value={formData.insurancePeriodGPS}
                            onChange={handleInputChange}
                            fullWidth
                            type="date"
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Mô tả GPS"
                            name="descriptionGPS"
                            value={formData.descriptionGPS}
                            onChange={handleInputChange}
                            fullWidth
                            multiline
                            rows={2}
                        />
                        <TextField
                            label="Thời hạn bảo hiểm 4G"
                            name="insurancePeriod4G"
                            value={formData.insurancePeriod4G}
                            onChange={handleInputChange}
                            fullWidth
                            type="date"
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Đơn vị bán bảo hiểm 4G"
                            name="insuranceSeller4G"
                            value={formData.insuranceSeller4G}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Mô tả"
                            name="description"
                            value={formData.description}
                            onChange={handleInputChange}
                            fullWidth
                            multiline
                            rows={2}
                        />
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedCar ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default CarManagement;
