import React from 'react';
import {
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    IconButton,
    Box
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

function DataTable(props) {
    const { columns = [], data = [], onEdit, onDelete, actions = true } = props;

    if (!columns.length || !data.length) {
        return null;
    }

    const renderActionButtons = (row) => {
        return (
            <Box>
                {onEdit && (
                    <IconButton onClick={() => onEdit(row)} color="primary">
                        <EditIcon />
                    </IconButton>
                )}
                {onDelete && (
                    <IconButton onClick={() => onDelete(row)} color="error">
                        <DeleteIcon />
                    </IconButton>
                )}
            </Box>
        );
    };

    return (
        <TableContainer component={Paper}>
            <Table>
                <TableHead>
                    <TableRow>
                        {columns.map((column) => (
                            <TableCell
                                key={column.field}
                                align={column.align || 'left'}
                            >
                                {column.headerName}
                            </TableCell>
                        ))}
                        {actions && <TableCell align="right">Thao t√°c</TableCell>}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {data.map((row, index) => (
                        <TableRow key={row._id || index}>
                            {columns.map((column) => (
                                <TableCell
                                    key={column.field}
                                    align={column.align || 'left'}
                                >
                                    {/* {column.renderCell ? column.renderCell(row) : row[column.field]} */}
                                </TableCell>
                            ))}
                            {actions && (
                                <TableCell align="right">
                                    {renderActionButtons(row)}
                                </TableCell>
                            )}
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}

export default DataTable;
