import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Alert,
    Snackbar,
} from '@mui/material';
import {
    Add as AddIcon,
} from '@mui/icons-material';
import DataTable from '../components/DataTable';
import axios from 'axios';

const CarManagement = () => {
    const [cars, setCars] = useState([]);
    const [teams, setTeams] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedCar, setSelectedCar] = useState(null);
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'success'
    });
    const [formData, setFormData] = useState({
        carType: '',
        bks: '',
        team: '',
        yearOfManufacture: '',
        registrationPeriod: '',
        registrationName: '',
        valuation: '',
        insurancePeriodTNDS: '',
        insuranceSellerTNDS: '',
        insurancePeriodBHVC: '',
        insuranceSellerBHVC: '',
        insurancePeriodGPS: '',
        descriptionGPS: '',
        insurancePeriod4G: '',
        insuranceSeller4G: '',
        description: '',
    });

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        return {
            headers: {
                'Authorization': token
            }
        };
    };

    useEffect(() => {
        fetchCars();
        fetchTeams();
    }, []);

    const fetchCars = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/car', getAuthConfig());
            console.log('API Response:', response);
            console.log('Cars Data:', response.data);
            setCars(response.data.data || []);
        } catch (error) {
            console.error('Error fetching cars:', error);
            showSnackbar('Lỗi khi tải dữ liệu xe', 'error');
        }
    };

    const fetchTeams = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/team', getAuthConfig());
            setTeams(response.data || []);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu team', 'error');
        }
    };

    const handleOpenDialog = (car = null) => {
        if (car) {
            setSelectedCar(car);
            setFormData({
                carType: car.carType || '',
                bks: car.bks || '',
                team: car.team || '',
                yearOfManufacture: car.yearOfManufacture || '',
                registrationPeriod: car.registrationPeriod || '',
                registrationName: car.registrationName || '',
                valuation: car.valuation || '',
                insurancePeriodTNDS: car.insurancePeriodTNDS || '',
                insuranceSellerTNDS: car.insuranceSellerTNDS || '',
                insurancePeriodBHVC: car.insurancePeriodBHVC || '',
                insuranceSellerBHVC: car.insuranceSellerBHVC || '',
                insurancePeriodGPS: car.insurancePeriodGPS || '',
                descriptionGPS: car.descriptionGPS || '',
                insurancePeriod4G: car.insurancePeriod4G || '',
                insuranceSeller4G: car.insuranceSeller4G || '',
                description: car.description || '',
            });
        } else {
            setSelectedCar(null);
            setFormData({
                carType: '',
                bks: '',
                team: '',
                yearOfManufacture: '',
                registrationPeriod: '',
                registrationName: '',
                valuation: '',
                insurancePeriodTNDS: '',
                insuranceSellerTNDS: '',
                insurancePeriodBHVC: '',
                insuranceSellerBHVC: '',
                insurancePeriodGPS: '',
                descriptionGPS: '',
                insurancePeriod4G: '',
                insuranceSeller4G: '',
                description: '',
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedCar(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            if (selectedCar) {
                await axios.put(`http://localhost:3076/api/vanminh/car/${selectedCar._id}`, formData, getAuthConfig());
                showSnackbar('Cập nhật xe thành công');
            } else {
                await axios.post('http://localhost:3076/api/vanminh/car', formData, getAuthConfig());
                showSnackbar('Thêm xe thành công');
            }
            handleCloseDialog();
            fetchCars();
        } catch (error) {
            showSnackbar('Có lỗi xảy ra', 'error');
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3076/api/vanminh/car/${id}`, getAuthConfig());
            showSnackbar('Xóa xe thành công');
            fetchCars();
        } catch (error) {
            showSnackbar('Lỗi khi xóa xe', 'error');
        }
    };

    const handleCloseSnackbar = () => {
        setSnackbar(prev => ({ ...prev, open: false }));
    };

    const showSnackbar = (message, severity = 'success') => {
        setSnackbar({ open: true, message, severity });
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return date.toLocaleDateString('vi-VN');
    };

    const formatCurrency = (value) => {
        if (!value) return '';
        return new Intl.NumberFormat('vi-VN', {
            style: 'currency',
            currency: 'VND'
        }).format(value);
    };

    const columns = [
        { field: 'carType', headerName: 'Loại xe' },
        { field: 'bks', headerName: 'Biển số xe' },
        { 
            field: 'team', 
            headerName: 'Team',
            renderCell: (row) => row.team?.label || ''
        },
        { 
            field: 'yearOfManufacture', 
            headerName: 'Năm sản xuất',
            renderCell: (row) => formatDate(row.yearOfManufacture)
        },
        { 
            field: 'registrationPeriod', 
            headerName: 'Thời hạn đăng kiểm',
            renderCell: (row) => formatDate(row.registrationPeriod)
        },
        { field: 'registrationName', headerName: 'Tên đăng ký' },
        { 
            field: 'valuation', 
            headerName: 'Định giá',
            renderCell: (row) => formatCurrency(row.valuation)
        }
    ];

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý xe</Typography>
                <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => handleOpenDialog()}
                >
                    Thêm mới
                </Button>
            </Box>

            <Paper sx={{ mb: 2 }}>
                <DataTable
                    columns={columns}
                    data={cars}
                    onEdit={handleOpenDialog}
                    onDelete={handleDelete}
                />
            </Paper>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                <DialogTitle>
                    {selectedCar ? 'Cập nhật xe' : 'Thêm mới xe'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Loại xe"
                            name="carType"
                            value={formData.carType}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Biển số xe"
                            name="bks"
                            value={formData.bks}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <FormControl fullWidth>
                            <InputLabel>Team</InputLabel>
                            <Select
                                name="team"
                                value={formData.team}
                                onChange={handleInputChange}
                                label="Team"
                            >
                                {teams.map((team) => (
                                    <MenuItem key={team._id} value={team._id}>
                                        {team.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <TextField
                            label="Năm sản xuất"
                            name="yearOfManufacture"
                            type="date"
                            value={formData.yearOfManufacture}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Thời hạn đăng kiểm"
                            name="registrationPeriod"
                            type="date"
                            value={formData.registrationPeriod}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Tên đăng ký"
                            name="registrationName"
                            value={formData.registrationName}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Định giá"
                            name="valuation"
                            type="number"
                            value={formData.valuation}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Thời hạn bảo hiểm TNDS"
                            name="insurancePeriodTNDS"
                            type="date"
                            value={formData.insurancePeriodTNDS}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Nhà bán bảo hiểm TNDS"
                            name="insuranceSellerTNDS"
                            value={formData.insuranceSellerTNDS}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Thời hạn bảo hiểm BHVC"
                            name="insurancePeriodBHVC"
                            type="date"
                            value={formData.insurancePeriodBHVC}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Nhà bán bảo hiểm BHVC"
                            name="insuranceSellerBHVC"
                            value={formData.insuranceSellerBHVC}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Thời hạn bảo hiểm GPS"
                            name="insurancePeriodGPS"
                            type="date"
                            value={formData.insurancePeriodGPS}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Mô tả GPS"
                            name="descriptionGPS"
                            value={formData.descriptionGPS}
                            onChange={handleInputChange}
                            fullWidth
                            multiline
                            rows={2}
                        />
                        <TextField
                            label="Thời hạn bảo hiểm 4G"
                            name="insurancePeriod4G"
                            type="date"
                            value={formData.insurancePeriod4G}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Nhà bán bảo hiểm 4G"
                            name="insuranceSeller4G"
                            value={formData.insuranceSeller4G}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Mô tả"
                            name="description"
                            value={formData.description}
                            onChange={handleInputChange}
                            fullWidth
                            multiline
                            rows={2}
                        />
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedCar ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>

            <Snackbar
                open={snackbar.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert
                    onClose={handleCloseSnackbar}
                    severity={snackbar.severity}
                    sx={{ width: '100%' }}
                >
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default CarManagement;
