import React, { useState } from 'react';
import {
    Box,
    Drawer,
    AppBar,
    Toolbar,
    List,
    Typography,
    Divider,
    IconButton,
    ListItem,
    ListItemButton,
    ListItemIcon,
    ListItemText,
    useTheme,
    Avatar,
    Badge,
    Menu,
    MenuItem,
    Tooltip,
} from '@mui/material';
import {
    Menu as MenuIcon,
    ChevronLeft as ChevronLeftIcon,
    Dashboard as DashboardIcon,
    People as PeopleIcon,
    Business as BusinessIcon,
    DirectionsCar as CarIcon,
    Inventory as InventoryIcon,
    Notifications as NotificationsIcon,
    AccountCircle as AccountCircleIcon,
    Group as GroupIcon,
    MeetingRoom as MeetingRoomIcon,
    Category as CategoryIcon,
    LocalShipping as LocalShippingIcon,
    SwapHoriz as SwapHorizIcon,
} from '@mui/icons-material';
import { useNavigate, useLocation } from 'react-router-dom';

const drawerWidth = 240;

const menuItems = [
    { text: 'Trang chủ', icon: <DashboardIcon />, path: '/' },
    { text: 'Quản lý nhân sự', icon: <PeopleIcon />, path: '/users' },
    { text: 'Quản lý đơn vị', icon: <CategoryIcon />, path: '/units' },
    { text: 'Quản lý phòng ban', icon: <BusinessIcon />, path: '/departments' },
    { text: 'Quản lý văn phòng', icon: <MeetingRoomIcon />, path: '/offices' },
    { text: 'Quản lý team', icon: <GroupIcon />, path: '/teams' },
    { text: 'Quản lý xe', icon: <CarIcon />, path: '/cars' },
    { text: 'Quản lý tài sản', icon: <InventoryIcon />, path: '/assets' },
    { text: 'Điều chuyển kho', icon: <SwapHorizIcon />, path: '/asset-transfers' },
];

const Layout = ({ children }) => {
    const [open, setOpen] = useState(true);
    const [anchorEl, setAnchorEl] = useState(null);
    const theme = useTheme();
    const navigate = useNavigate();
    const location = useLocation();

    const handleDrawerToggle = () => {
        setOpen(!open);
    };

    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    return (
        <Box sx={{ display: 'flex', minHeight: '100vh' }}>
            <AppBar
                position="fixed"
                sx={{
                    width: `calc(100% - ${open ? drawerWidth : 0}px)`,
                    ml: `${open ? drawerWidth : 0}px`,
                    transition: theme.transitions.create(['margin', 'width'], {
                        easing: theme.transitions.easing.sharp,
                        duration: theme.transitions.duration.leavingScreen,
                    }),
                    backgroundColor: 'white',
                    color: theme.palette.text.primary,
                }}
            >
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="open drawer"
                        edge="start"
                        onClick={handleDrawerToggle}
                        sx={{ mr: 2 }}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1 }}>
                        Van Minh Group
                    </Typography>
                    <Tooltip title="Thông báo">
                        <IconButton color="inherit">
                            <Badge badgeContent={4} color="error">
                                <NotificationsIcon />
                            </Badge>
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Tài khoản">
                        <IconButton
                            edge="end"
                            aria-label="account of current user"
                            onClick={handleProfileMenuOpen}
                            color="inherit"
                        >
                            <Avatar sx={{ width: 32, height: 32, bgcolor: theme.palette.primary.main }}>
                                <AccountCircleIcon />
                            </Avatar>
                        </IconButton>
                    </Tooltip>
                    <Menu
                        anchorEl={anchorEl}
                        open={Boolean(anchorEl)}
                        onClose={handleMenuClose}
                        PaperProps={{
                            sx: {
                                mt: 1.5,
                                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                            },
                        }}
                    >
                        <MenuItem onClick={handleMenuClose}>Thông tin cá nhân</MenuItem>
                        <MenuItem onClick={handleMenuClose}>Đổi mật khẩu</MenuItem>
                        <Divider />
                        <MenuItem onClick={handleMenuClose}>Đăng xuất</MenuItem>
                    </Menu>
                </Toolbar>
            </AppBar>
            <Drawer
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    '& .MuiDrawer-paper': {
                        width: drawerWidth,
                        boxSizing: 'border-box',
                        backgroundColor: theme.palette.primary.main,
                        color: theme.palette.primary.contrastText,
                    },
                }}
                variant="persistent"
                anchor="left"
                open={open}
            >
                <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    p: 2, 
                    justifyContent: 'space-between',
                    backgroundColor: theme.palette.primary.dark,
                }}>
                    <Typography variant="h6" noWrap component="div">
                        Menu
                    </Typography>
                    <IconButton onClick={handleDrawerToggle} sx={{ color: 'white' }}>
                        <ChevronLeftIcon />
                    </IconButton>
                </Box>
                <Divider sx={{ backgroundColor: 'rgba(255,255,255,0.1)' }} />
                <List>
                    {menuItems.map((item) => (
                        <ListItem key={item.text} disablePadding>
                            <ListItemButton
                                onClick={() => navigate(item.path)}
                                selected={location.pathname === item.path}
                                sx={{
                                    '&:hover': {
                                        backgroundColor: 'rgba(255,255,255,0.1)',
                                    },
                                    '&.Mui-selected': {
                                        backgroundColor: 'rgba(255,255,255,0.2)',
                                    },
                                }}
                            >
                                <ListItemIcon sx={{ color: 'inherit' }}>
                                    {item.icon}
                                </ListItemIcon>
                                <ListItemText 
                                    primary={item.text}
                                    primaryTypographyProps={{
                                        sx: { fontWeight: 500 }
                                    }}
                                />
                            </ListItemButton>
                        </ListItem>
                    ))}
                </List>
            </Drawer>
            <Box
                component="main"
                sx={{
                    flexGrow: 1,
                    p: 3,
                    width: `calc(100% - ${open ? drawerWidth : 0}px)`,
                    ml: `${open ? drawerWidth : 0}px`,
                    transition: theme.transitions.create(['margin', 'width'], {
                        easing: theme.transitions.easing.sharp,
                        duration: theme.transitions.duration.leavingScreen,
                    }),
                    backgroundColor: theme.palette.background.default,
                    minHeight: '100vh',
                }}
            >
                <Toolbar />
                {children}
            </Box>
        </Box>
    );
};

export default Layout;
