export const tableStyles = {
    paper: {
        mb: 2, 
        overflow: 'hidden',
        borderRadius: 2,
        boxShadow: 3,
        '& .MuiTableRow-root:hover': {
            backgroundColor: 'rgba(0, 0, 0, 0.04)'
        },
        '& .MuiTableCell-head': {
            backgroundColor: '#FFA726',
            color: '#fff',
            fontWeight: 'bold',
            fontSize: '0.9rem',
            padding: '12px 16px',
            textTransform: 'uppercase',
            letterSpacing: '0.5px',
            borderBottom: 'none',
            whiteSpace: 'nowrap'
        },
        '& .MuiTableCell-root': {
            borderBottom: '1px solid rgba(224, 224, 224, 1)',
            padding: '8px 16px',
            fontSize: '0.875rem'
        },
        '& .MuiTablePagination-root': {
            backgroundColor: '#f5f5f5',
            borderTop: '1px solid rgba(224, 224, 224, 1)'
        }
    },
    cellContent: {
        whiteSpace: 'nowrap',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        width: '100%'
    },
    pageContainer: {
        p: 3
    },
    headerContainer: {
        display: 'flex',
        justifyContent: 'space-between',
        mb: 3
    },
    tableContainer: {
        width: '100%'
    }
};
