import React, { useState, useEffect, useCallback } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Alert,
    Snackbar,
    TableContainer,
    Tooltip,
    Grid
} from '@mui/material';
import {
    Add as AddIcon,
    ImportExport as ImportExportIcon
} from '@mui/icons-material';
import DataTable from '../components/DataTable';
import axios from 'axios';
import assetTransferApi from '../api/assetTransferApi';
import officeApi from '../api/officeApi';

const AssetManagement = () => {
    const [assets, setAssets] = useState([]);
    const [units, setUnits] = useState([]);
    const [assetTypes, setAssetTypes] = useState([]);
    const [offices, setOffices] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedAsset, setSelectedAsset] = useState(null);
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'success'
    });
    const [formData, setFormData] = useState({
        code: '',
        quantity: '',
        unit: '',
        name: '',
        modelOrSeries: '',
        assetType: '',
        dateOfPurchase: '',
        warranty: '',
        expirationDate: '',
        price: '',
        depreciation: '',
        supplier: '',
        addressNCC: '',
        phoneNCC: '',
        assetLocation: '',
        managementOffice: '',
        description: '',
        seeMore: {},
    });
    const [pagination, setPagination] = useState({
        page: 1,
        limit: 10,
        total: 0,
    });
    const [transferDialog, setTransferDialog] = useState(false);
    const [transferType, setTransferType] = useState('IMPORT');
    const [transferData, setTransferData] = useState({
        assetId: '',
        quantity: 0,
        fromOfficeId: '',
        toOfficeId: '',
        note: '',
        reason: 'NEW_IMPORT'
    });

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        return {
            headers: {
                'Authorization': token
            }
        };
    };

    useEffect(() => {
        fetchAssets();
        fetchUnits();
        fetchAssetTypes();
        fetchOffices();
    }, []);

    const fetchAssets = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/asset', getAuthConfig());
            console.log('API Response:', response);
            console.log('Assets Data:', response.data);
            setAssets(response.data.data || []);
            setPagination(prev => ({
                ...prev,
                total: response.data.total
            }));
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu tài sản', 'error');
        }
    };

    const fetchUnits = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/unit', getAuthConfig());
            setUnits(response.data || []);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu đơn vị', 'error');
        }
    };

    const fetchAssetTypes = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/asset-type', getAuthConfig());
            setAssetTypes(response.data || []);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu loại tài sản', 'error');
        }
    };

    const fetchOffices = async () => {
        try {
            const response = await axios.get('/api/vanminh/office');
            setOffices(response.data.offices || []);
        } catch (error) {
            console.error('Error fetching offices:', error);
            setSnackbar({
                open: true,
                message: 'Lỗi khi tải danh sách văn phòng',
                severity: 'error'
            });
        }
    };

    const handleOpenDialog = (asset = null) => {
        if (asset) {
            setSelectedAsset(asset);
            setFormData({
                code: asset.code || '',
                quantity: asset.quantity || '',
                unit: asset.unit || '',
                name: asset.name || '',
                modelOrSeries: asset.modelOrSeries || '',
                assetType: asset.assetType || '',
                dateOfPurchase: asset.dateOfPurchase ? asset.dateOfPurchase.split('T')[0] : '',
                warranty: asset.warranty || '',
                expirationDate: asset.expirationDate ? asset.expirationDate.split('T')[0] : '',
                price: asset.price || '',
                depreciation: asset.depreciation || '',
                supplier: asset.supplier || '',
                addressNCC: asset.addressNCC || '',
                phoneNCC: asset.phoneNCC || '',
                assetLocation: asset.assetLocation || '',
                managementOffice: asset.managementOffice || '',
                description: asset.description || '',
                seeMore: asset.seeMore || {},
            });
        } else {
            setSelectedAsset(null);
            setFormData({
                code: '',
                quantity: '',
                unit: '',
                name: '',
                modelOrSeries: '',
                assetType: '',
                dateOfPurchase: '',
                warranty: '',
                expirationDate: '',
                price: '',
                depreciation: '',
                supplier: '',
                addressNCC: '',
                phoneNCC: '',
                assetLocation: '',
                managementOffice: '',
                description: '',
                seeMore: {},
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedAsset(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            if (selectedAsset) {
                await axios.put(`http://localhost:3076/api/vanminh/asset/${selectedAsset._id}`, formData, getAuthConfig());
                showSnackbar('Cập nhật tài sản thành công');
            } else {
                await axios.post('http://localhost:3076/api/vanminh/asset', formData, getAuthConfig());
                showSnackbar('Thêm tài sản thành công');
            }
            handleCloseDialog();
            fetchAssets();
        } catch (error) {
            showSnackbar('Có lỗi xảy ra', 'error');
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3076/api/vanminh/asset/${id}`, getAuthConfig());
            showSnackbar('Xóa tài sản thành công');
            fetchAssets();
        } catch (error) {
            showSnackbar('Lỗi khi xóa tài sản', 'error');
        }
    };

    const handleCloseSnackbar = () => {
        setSnackbar(prev => ({ ...prev, open: false }));
    };

    const showSnackbar = (message, severity = 'success') => {
        setSnackbar({ open: true, message, severity });
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        return new Date(dateString).toLocaleDateString();
    };

    const formatCurrency = (value) => {
        if (!value) return '';
        return value.toLocaleString('vi-VN') + ' VNĐ';
    };

    const formatPrice = (price) => {
        return new Intl.NumberFormat('vi-VN', { 
            style: 'currency', 
            currency: 'VND' 
        }).format(price);
    };

    const columns = [
        { 
            field: 'code', 
            headerName: 'Mã tài sản',
            width: 100,
            renderCell: (row) => (
                <Tooltip title={row.code || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.code}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'name', 
            headerName: 'Tên tài sản',
            width: 130,
            renderCell: (row) => (
                <Tooltip title={row.name || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.name}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'assetType', 
            headerName: 'Loại tài sản',
            width: 110,
            renderCell: (row) => (
                <Tooltip title={row.assetType?.label || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.assetType?.label || ''}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'modelOrSeries', 
            headerName: 'Model/Series',
            width: 100,
            renderCell: (row) => (
                <Tooltip title={row.modelOrSeries || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.modelOrSeries}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'quantity', 
            headerName: 'SL',
            width: 60,
            align: 'right'
        },
        { 
            field: 'unit', 
            headerName: 'ĐV',
            width: 60,
            renderCell: (row) => (
                <Tooltip title={row.unit?.label || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.unit?.label || ''}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'price', 
            headerName: 'Giá',
            width: 100,
            align: 'right',
            renderCell: (row) => formatPrice(row.price)
        },
        { 
            field: 'supplier', 
            headerName: 'Nhà cung cấp',
            width: 120,
            renderCell: (row) => (
                <Tooltip title={row.supplier || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.supplier}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'assetLocation', 
            headerName: 'Vị trí TS',
            width: 100,
            renderCell: (row) => (
                <Tooltip title={row.assetLocation?.code || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.assetLocation?.code || ''}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'managementOffice', 
            headerName: 'VP Quản lý',
            width: 100,
            renderCell: (row) => (
                <Tooltip title={row.managementOffice?.code || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.managementOffice?.code || ''}
                    </Box>
                </Tooltip>
            )
        },
        { 
            field: 'description', 
            headerName: 'Mô tả',
            width: 150,
            renderCell: (row) => (
                <Tooltip title={row.description || ''}>
                    <Box sx={{ 
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis',
                        width: '100%'
                    }}>
                        {row.description || ''}
                    </Box>
                </Tooltip>
            )
        },
        {
            field: 'actions',
            headerName: 'Thao tác',
            width: 100,
            renderCell: (row) => (
                <Button
                    size="small"
                    variant="contained"
                    color="secondary"
                    onClick={() => handleTransferDialogOpen(row)}
                >
                    Xuất kho
                </Button>
            )
        }
    ];

    const handlePageChange = (page) => {
        setPagination(prev => ({ ...prev, page }));
        fetchAssets();
    };

    const handleLimitChange = (limit) => {
        setPagination(prev => ({ ...prev, limit }));
        fetchAssets();
    };

    const handleTransferDialogOpen = (asset, type = 'EXPORT') => {
        setTransferType(type);
        setTransferData({
            assetId: asset._id,
            quantity: 1,
            fromOfficeId: asset.managementOffice?._id || '',
            toOfficeId: '',
            note: '',
            reason: type === 'IMPORT' ? 'NEW_IMPORT' : 'TRANSFER'
        });
        setTransferDialog(true);
    };

    const handleTransferDialogClose = () => {
        setTransferDialog(false);
        setTransferData({
            assetId: '',
            quantity: 0,
            fromOfficeId: '',
            toOfficeId: '',
            note: '',
            reason: 'NEW_IMPORT'
        });
    };

    const handleTransferChange = (event) => {
        const { name, value } = event.target;
        setTransferData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleTransferSubmit = async () => {
        try {
            if (transferType === 'IMPORT') {
                await assetTransferApi.importAsset(transferData);
                showSnackbar('Nhập kho thành công', 'success');
            } else {
                await assetTransferApi.exportAsset(transferData);
                showSnackbar('Xuất kho thành công', 'success');
            }
            handleTransferDialogClose();
            fetchAssets();
        } catch (error) {
            showSnackbar(error.response?.data?.message || 'Lỗi khi thực hiện xuất/nhập kho', 'error');
        }
    };

    const renderTransferDialog = () => {
        const selectedAsset = assets.find(a => a._id === transferData.assetId);
        const availableOffices = offices.filter(office => office._id !== transferData.fromOfficeId);

        return (
            <Dialog open={transferDialog} onClose={handleTransferDialogClose} maxWidth="md" fullWidth>
                <DialogTitle>
                    {transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho'}
                </DialogTitle>
                <DialogContent>
                    <Grid container spacing={2} sx={{ mt: 1 }}>
                        {transferType === 'EXPORT' && selectedAsset && (
                            <>
                                <Grid item xs={12}>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Tài sản:</strong> {selectedAsset.name} - {selectedAsset.code}
                                    </Typography>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Số lượng hiện có:</strong> {selectedAsset.quantity} {selectedAsset.unit?.label}
                                    </Typography>
                                </Grid>
                                <Grid item xs={6}>
                                    <TextField
                                        fullWidth
                                        label="Văn phòng nguồn"
                                        value={offices.find(o => o._id === transferData.fromOfficeId)?.label || ''}
                                        disabled
                                    />
                                </Grid>
                                <Grid item xs={6}>
                                    <TextField
                                        fullWidth
                                        select
                                        label="Văn phòng đích"
                                        name="toOfficeId"
                                        value={transferData.toOfficeId}
                                        onChange={handleTransferChange}
                                        required
                                        error={!transferData.toOfficeId}
                                        helperText={!transferData.toOfficeId ? "Vui lòng chọn văn phòng đích" : ""}
                                    >
                                        {availableOffices.map((office) => (
                                            <MenuItem key={office._id} value={office._id}>
                                                {office.label}
                                            </MenuItem>
                                        ))}
                                    </TextField>
                                </Grid>
                            </>
                        )}
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                type="number"
                                label="Số lượng"
                                name="quantity"
                                value={transferData.quantity}
                                onChange={handleTransferChange}
                                required
                                error={transferData.quantity <= 0 || transferData.quantity > (selectedAsset?.quantity || 0)}
                                helperText={
                                    transferData.quantity <= 0 
                                        ? "Số lượng phải lớn hơn 0"
                                        : transferData.quantity > (selectedAsset?.quantity || 0)
                                        ? "Số lượng không đủ trong kho"
                                        : ""
                                }
                                inputProps={{ 
                                    min: 1,
                                    max: selectedAsset?.quantity || 999999
                                }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                select
                                label="Lý do"
                                name="reason"
                                value={transferData.reason}
                                onChange={handleTransferChange}
                                required
                            >
                                {transferType === 'EXPORT' && (
                                    <>
                                        <MenuItem value="TRANSFER">Điều chuyển</MenuItem>
                                        <MenuItem value="RETURN">Trả về</MenuItem>
                                        <MenuItem value="MAINTENANCE">Bảo trì</MenuItem>
                                    </>
                                )}
                            </TextField>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                multiline
                                rows={3}
                                label="Ghi chú"
                                name="note"
                                value={transferData.note}
                                onChange={handleTransferChange}
                            />
                        </Grid>
                    </Grid>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleTransferDialogClose}>Hủy</Button>
                    <Button 
                        onClick={handleTransferSubmit}
                        variant="contained"
                        color="primary"
                        disabled={
                            !transferData.toOfficeId ||
                            transferData.quantity <= 0 ||
                            transferData.quantity > (selectedAsset?.quantity || 0)
                        }
                    >
                        {transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho'}
                    </Button>
                </DialogActions>
            </Dialog>
        );
    };

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý tài sản</Typography>
                <Box>
                    <Button
                        variant="contained"
                        color="secondary"
                        startIcon={<ImportExportIcon />}
                        onClick={() => handleTransferDialogOpen(null, 'IMPORT')}
                        sx={{ mr: 1 }}
                    >
                        Nhập kho
                    </Button>
                    <Button
                        variant="contained"
                        color="secondary"
                        startIcon={<ImportExportIcon />}
                        onClick={() => handleTransferDialogOpen(null, 'EXPORT')}
                        sx={{ mr: 1 }}
                    >
                        Xuất kho
                    </Button>
                    <Button
                        variant="contained"
                        startIcon={<AddIcon />}
                        onClick={() => handleOpenDialog()}
                    >
                        Thêm mới
                    </Button>
                </Box>
            </Box>

            <Paper 
                sx={{ 
                    mb: 2, 
                    overflow: 'hidden',
                    borderRadius: 2,
                    boxShadow: 3,
                    '& .MuiTableRow-root:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                    },
                    '& .MuiTableCell-head': {
                        backgroundColor: '#FFA726',
                        color: '#fff',
                        fontWeight: 'bold',
                        fontSize: '0.9rem',
                        padding: '12px 16px',
                        textTransform: 'uppercase',
                        letterSpacing: '0.5px',
                        borderBottom: 'none',
                        whiteSpace: 'nowrap'
                    },
                    '& .MuiTableCell-root': {
                        borderBottom: '1px solid rgba(224, 224, 224, 1)',
                        padding: '8px 16px',
                        fontSize: '0.875rem'
                    },
                    '& .MuiTablePagination-root': {
                        backgroundColor: '#f5f5f5',
                        borderTop: '1px solid rgba(224, 224, 224, 1)'
                    }
                }}
            >
                <TableContainer>
                    <DataTable
                        columns={columns}
                        data={assets}
                        onEdit={handleOpenDialog}
                        onDelete={handleDelete}
                        pagination={{
                            page: pagination.page,
                            limit: pagination.limit,
                            total: pagination.total,
                            onPageChange: handlePageChange,
                            onLimitChange: handleLimitChange
                        }}
                    />
                </TableContainer>
            </Paper>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                <DialogTitle>
                    {selectedAsset ? 'Cập nhật tài sản' : 'Thêm mới tài sản'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Mã tài sản"
                            name="code"
                            value={formData.code}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Tên tài sản"
                            name="name"
                            value={formData.name}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Model/Series"
                            name="modelOrSeries"
                            value={formData.modelOrSeries}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <FormControl fullWidth>
                            <InputLabel>Loại tài sản</InputLabel>
                            <Select
                                name="assetType"
                                value={formData.assetType}
                                onChange={handleInputChange}
                                label="Loại tài sản"
                            >
                                {assetTypes.map((type) => (
                                    <MenuItem key={type._id} value={type._id}>
                                        {type.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <Box sx={{ display: 'flex', gap: 2 }}>
                            <TextField
                                label="Số lượng"
                                name="quantity"
                                type="number"
                                value={formData.quantity}
                                onChange={handleInputChange}
                                fullWidth
                            />
                            <FormControl fullWidth>
                                <InputLabel>Đơn vị</InputLabel>
                                <Select
                                    name="unit"
                                    value={formData.unit}
                                    onChange={handleInputChange}
                                    label="Đơn vị"
                                >
                                    {units.map((unit) => (
                                        <MenuItem key={unit._id} value={unit._id}>
                                            {unit.label}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Box>
                        <TextField
                            label="Giá"
                            name="price"
                            type="number"
                            value={formData.price}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Ngày mua"
                            name="dateOfPurchase"
                            type="date"
                            value={formData.dateOfPurchase}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Thời hạn bảo hành (tháng)"
                            name="warranty"
                            type="number"
                            value={formData.warranty}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Ngày hết hạn"
                            name="expirationDate"
                            type="date"
                            value={formData.expirationDate}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Khấu hao"
                            name="depreciation"
                            value={formData.depreciation}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Nhà cung cấp"
                            name="supplier"
                            value={formData.supplier}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Địa chỉ nhà cung cấp"
                            name="addressNCC"
                            value={formData.addressNCC}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Số điện thoại nhà cung cấp"
                            name="phoneNCC"
                            value={formData.phoneNCC}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <FormControl fullWidth>
                            <InputLabel>Vị trí tài sản</InputLabel>
                            <Select
                                name="assetLocation"
                                value={formData.assetLocation}
                                onChange={handleInputChange}
                                label="Vị trí tài sản"
                            >
                                {offices.map((office) => (
                                    <MenuItem key={office._id} value={office._id}>
                                        {office.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <FormControl fullWidth>
                            <InputLabel>Văn phòng quản lý</InputLabel>
                            <Select
                                name="managementOffice"
                                value={formData.managementOffice}
                                onChange={handleInputChange}
                                label="Văn phòng quản lý"
                            >
                                {offices.map((office) => (
                                    <MenuItem key={office._id} value={office._id}>
                                        {office.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <TextField
                            label="Mô tả"
                            name="description"
                            value={formData.description}
                            onChange={handleInputChange}
                            fullWidth
                            multiline
                            rows={3}
                        />
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedAsset ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>

            {renderTransferDialog()}

            <Snackbar
                open={snackbar.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert
                    onClose={handleCloseSnackbar}
                    severity={snackbar.severity}
                    sx={{ width: '100%' }}
                >
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default AssetManagement;
