import React, { useState, useEffect, useCallback } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TablePagination,
    IconButton,
    Tooltip,
    Alert,
    Snackbar,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Grid,
} from '@mui/material';
import {
    Add as AddIcon,
    Edit as EditIcon,
    Delete as DeleteIcon,
    Search as SearchIcon,
} from '@mui/icons-material';
import userApi from '../api/userApi';
import departmentApi from '../api/departmentApi';
import officeApi from '../api/officeApi';
import teamApi from '../api/teamApi';

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [total, setTotal] = useState(0);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

    // Dữ liệu từ database
    const [departments, setDepartments] = useState([]);
    const [offices, setOffices] = useState([]);
    const [teams, setTeams] = useState([]);

    // Form data
    const [formData, setFormData] = useState({
        phone: '',
        password: '',
        name: '',
        departmentID: '',
        teamID: '',
        officeID: '',
        ruler: '',
        avatar: '',
        dateOfBirth: '',
        email: '',
        status: true,
        position: '',
    });

    // Load dữ liệu từ database
    const loadData = useCallback(async () => {
        try {
            const [deptResponse, officeResponse, teamResponse] = await Promise.all([
                departmentApi.getAll(),
                officeApi.getAll(),
                teamApi.getAll()
            ]);
            setDepartments(deptResponse);
            setOffices(officeResponse);
            setTeams(teamResponse);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu', 'error');
        }
    }, []);

    useEffect(() => {
        loadData();
    }, [loadData]);

    const fetchUsers = useCallback(async () => {
        try {
            const response = await userApi.getAll(page + 1, rowsPerPage);
            setUsers(response.account);
            setTotal(response.total);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu', 'error');
        }
    }, [page, rowsPerPage]);

    useEffect(() => {
        fetchUsers();
    }, [fetchUsers]);

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const handleOpenDialog = (user = null) => {
        if (user) {
            setSelectedUser(user);
            setFormData({
                ...user,
                password: '', // Không hiển thị mật khẩu
            });
        } else {
            setSelectedUser(null);
            setFormData({
                phone: '',
                password: '',
                name: '',
                departmentID: '',
                teamID: '',
                officeID: '',
                ruler: '',
                avatar: '',
                dateOfBirth: '',
                email: '',
                status: true,
                position: '',
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedUser(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        // Nếu thay đổi phòng ban, load lại danh sách văn phòng và team
        if (name === 'departmentID') {
            loadOfficesAndTeams(value);
        }
    };

    const loadOfficesAndTeams = async (departmentId) => {
        try {
            const [officeResponse, teamResponse] = await Promise.all([
                officeApi.getByDepartment(departmentId),
                teamApi.getByDepartment(departmentId)
            ]);
            setOffices(officeResponse);
            setTeams(teamResponse);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu', 'error');
        }
    };

    const handleSubmit = async () => {
        try {
            if (selectedUser) {
                await userApi.update(selectedUser._id, formData);
                showSnackbar('Cập nhật thành công');
            } else {
                await userApi.create(formData);
                showSnackbar('Thêm mới thành công');
            }
            handleCloseDialog();
            fetchUsers();
        } catch (error) {
            showSnackbar('Có lỗi xảy ra', 'error');
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa?')) {
            try {
                await userApi.delete([id]);
                showSnackbar('Xóa thành công');
                fetchUsers();
            } catch (error) {
                showSnackbar('Có lỗi xảy ra', 'error');
            }
        }
    };

    const showSnackbar = (message, severity = 'success') => {
        setSnackbar({ open: true, message, severity });
    };

    const handleCloseSnackbar = () => {
        setSnackbar(prev => ({ ...prev, open: false }));
    };

    const filteredUsers = users.filter(user =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.phone.includes(searchTerm)
    );

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý nhân sự</Typography>
                <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => handleOpenDialog()}
                >
                    Thêm mới
                </Button>
            </Box>

            <Paper sx={{ mb: 2 }}>
                <Box sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
                    <TextField
                        placeholder="Tìm kiếm theo tên hoặc số điện thoại"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        fullWidth
                        InputProps={{
                            startAdornment: <SearchIcon sx={{ mr: 1, color: 'text.secondary' }} />,
                        }}
                    />
                </Box>

                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>STT</TableCell>
                                <TableCell>Tên</TableCell>
                                <TableCell>Số điện thoại</TableCell>
                                <TableCell>Email</TableCell>
                                <TableCell>Phòng ban</TableCell>
                                <TableCell>Team</TableCell>
                                <TableCell>Văn phòng</TableCell>
                                <TableCell>Vị trí</TableCell>
                                <TableCell>Trạng thái</TableCell>
                                <TableCell>Thao tác</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {filteredUsers.map((user, index) => (
                                <TableRow key={user._id}>
                                    <TableCell>{index + 1}</TableCell>
                                    <TableCell>{user.name}</TableCell>
                                    <TableCell>{user.phone}</TableCell>
                                    <TableCell>{user.email}</TableCell>
                                    <TableCell>
                                        {departments.find(d => d._id === user.departmentID)?.label || ''}
                                    </TableCell>
                                    <TableCell>
                                        {teams.find(t => t._id === user.teamID)?.label || ''}
                                    </TableCell>
                                    <TableCell>
                                        {offices.find(o => o._id === user.officeID)?.label || ''}
                                    </TableCell>
                                    <TableCell>{user.position}</TableCell>
                                    <TableCell>
                                        {user.status ? 'Hoạt động' : 'Không hoạt động'}
                                    </TableCell>
                                    <TableCell>
                                        <Tooltip title="Sửa">
                                            <IconButton onClick={() => handleOpenDialog(user)}>
                                                <EditIcon />
                                            </IconButton>
                                        </Tooltip>
                                        <Tooltip title="Xóa">
                                            <IconButton onClick={() => handleDelete(user._id)}>
                                                <DeleteIcon />
                                            </IconButton>
                                        </Tooltip>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[5, 10, 25]}
                    component="div"
                    count={total}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
                <DialogTitle>
                    {selectedUser ? 'Cập nhật thông tin' : 'Thêm mới nhân viên'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    label="Họ và tên"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Số điện thoại"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    label="Mật khẩu"
                                    name="password"
                                    type="password"
                                    value={formData.password}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <FormControl fullWidth>
                                    <InputLabel>Phòng ban</InputLabel>
                                    <Select
                                        name="departmentID"
                                        value={formData.departmentID}
                                        onChange={handleInputChange}
                                        label="Phòng ban"
                                    >
                                        {departments.map((dept) => (
                                            <MenuItem key={dept._id} value={dept._id}>
                                                {dept.label}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <FormControl fullWidth>
                                    <InputLabel>Team</InputLabel>
                                    <Select
                                        name="teamID"
                                        value={formData.teamID}
                                        onChange={handleInputChange}
                                        label="Team"
                                    >
                                        {teams.map((team) => (
                                            <MenuItem key={team._id} value={team._id}>
                                                {team.label}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <FormControl fullWidth>
                                    <InputLabel>Văn phòng</InputLabel>
                                    <Select
                                        name="officeID"
                                        value={formData.officeID}
                                        onChange={handleInputChange}
                                        label="Văn phòng"
                                    >
                                        {offices.map((office) => (
                                            <MenuItem key={office._id} value={office._id}>
                                                {office.label}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    label="Vị trí"
                                    name="position"
                                    value={formData.position}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <FormControl fullWidth>
                                    <InputLabel>Trạng thái</InputLabel>
                                    <Select
                                        name="status"
                                        value={formData.status}
                                        onChange={handleInputChange}
                                        label="Trạng thái"
                                    >
                                        <MenuItem value={true}>Hoạt động</MenuItem>
                                        <MenuItem value={false}>Không hoạt động</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid>
                        </Grid>
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedUser ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>

            <Snackbar
                open={snackbar.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert
                    onClose={handleCloseSnackbar}
                    severity={snackbar.severity}
                    sx={{ width: '100%' }}
                >
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default UserManagement;
