import React, { useState, useEffect } from 'react';
import {
    Box,
    Typography,
    MenuItem,
    TextField,
    Grid,
    Chip,
    Paper,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Checkbox,
    IconButton,
    Tooltip,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TablePagination,
    CircularProgress
} from '@mui/material';
import DataTable from '../components/DataTable';
import axios from 'axios';
import DeleteIcon from '@mui/icons-material/Delete';

const AssetTransferHistory = () => {
    const [logs, setLogs] = useState([]);
    const [loading, setLoading] = useState(false);
    const [selectedLogs, setSelectedLogs] = useState([]);
    const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
    const [pagination, setPagination] = useState({
        page: 0,
        limit: 10,
        total: 0
    });

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        return {
            headers: { Authorization: `Bearer ${token}` }
        };
    };

    const fetchLogs = async () => {
        try {
            setLoading(true);
            const response = await axios.get(
                `http://localhost:3076/api/vanminh/asset-transfer/logs?page=${pagination.page + 1}&limit=${pagination.limit}`,
                getAuthConfig()
            );

            const formattedLogs = response.data?.data?.map(log => ({
                ...log,
                assetName: `${log.assetId.name || ''} (${log.assetId.code || ''})`,
                assetCode: log.assetId.code || '',
                transferType: log.transferType === 'import' ? 'Nhập kho' : 'Xuất kho',
                status: log.status === 'pending' ? 'Chờ xử lý' : 
                       log.status === 'approved' ? 'Đã duyệt' :
                       log.status === 'rejected' ? 'Từ chối' : log.status,
                createdAt: new Date(log.createdAt).toLocaleDateString('vi-VN')
            }));

            setLogs(formattedLogs || []);
            setPagination(prev => ({
                ...prev,
                total: response.data?.total || 0
            }));
        } catch (error) {
            console.error('Lỗi khi tải logs:', error);
            setLogs([]);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchLogs();
    }, [pagination.page, pagination.limit]);

    const handleChangePage = (event, newPage) => {
        setPagination(prev => ({ ...prev, page: newPage }));
    };

    const handleChangeRowsPerPage = (event) => {
        setPagination(prev => ({
            ...prev,
            page: 0,
            limit: parseInt(event.target.value, 10)
        }));
    };

    const handleSelectAllClick = (event) => {
        if (event.target.checked) {
            const newSelected = logs.map(log => log._id);
            setSelectedLogs(newSelected);
        } else {
            setSelectedLogs([]);
        }
    };

    const handleSelectLog = (id) => {
        const selectedIndex = selectedLogs.indexOf(id);
        let newSelected = [];

        if (selectedIndex === -1) {
            newSelected = [...selectedLogs, id];
        } else {
            newSelected = selectedLogs.filter(logId => logId !== id);
        }

        setSelectedLogs(newSelected);
    };

    const handleDeleteLogs = async () => {
        try {
            await axios.delete(
                'http://localhost:3076/api/vanminh/asset-transfer/logs',
                { 
                    ...getAuthConfig(),
                    data: { ids: selectedLogs }
                }
            );

            setSelectedLogs([]);
            setOpenDeleteDialog(false);
            fetchLogs();
        } catch (error) {
            console.error('Lỗi khi xóa logs:', error);
        }
    };

    const columns = [
        { 
            id: 'checkbox',
            label: <Checkbox
                indeterminate={selectedLogs.length > 0 && selectedLogs.length < logs.length}
                checked={logs.length > 0 && selectedLogs.length === logs.length}
                onChange={handleSelectAllClick}
            />,
            width: '5%'
        },
        { id: 'assetName', label: 'Tên tài sản', width: '25%' },
        { id: 'transferType', label: 'Loại', width: '15%' },
        { id: 'quantity', label: 'Số lượng', width: '15%' },
        { id: 'status', label: 'Trạng thái', width: '15%' },
        { id: 'createdAt', label: 'Ngày tạo', width: '15%' }
    ];

    return (
        <Box sx={{ width: '100%', p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                <Typography variant="h5">Lịch sử chuyển kho</Typography>
                {selectedLogs.length > 0 && (
                    <Tooltip title="Xóa các mục đã chọn">
                        <IconButton onClick={() => setOpenDeleteDialog(true)} color="error">
                            <DeleteIcon />
                        </IconButton>
                    </Tooltip>
                )}
            </Box>

            <Paper sx={{ width: '100%', overflow: 'hidden' }}>
                {loading ? (
                    <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
                        <CircularProgress />
                    </Box>
                ) : (
                    <>
                        <TableContainer>
                            <Table stickyHeader>
                                <TableHead>
                                    <TableRow>
                                        {columns.map(column => (
                                            <TableCell
                                                key={column.id}
                                                style={{ width: column.width }}
                                            >
                                                {column.label}
                                            </TableCell>
                                        ))}
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {logs.map(log => (
                                        <TableRow key={log._id}>
                                            <TableCell>
                                                <Checkbox
                                                    checked={selectedLogs.indexOf(log._id) !== -1}
                                                    onChange={() => handleSelectLog(log._id)}
                                                />
                                            </TableCell>
                                            <TableCell>{log.assetName}</TableCell>
                                            <TableCell>{log.transferType}</TableCell>
                                            <TableCell>{log.quantity}</TableCell>
                                            <TableCell>{log.status}</TableCell>
                                            <TableCell>{log.createdAt}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                        <TablePagination
                            component="div"
                            count={pagination.total}
                            page={pagination.page}
                            onPageChange={handleChangePage}
                            rowsPerPage={pagination.limit}
                            onRowsPerPageChange={handleChangeRowsPerPage}
                            labelRowsPerPage="Số hàng mỗi trang:"
                            labelDisplayedRows={({ from, to, count }) => 
                                `${from}-${to} của ${count !== -1 ? count : `hơn ${to}`}`}
                        />
                    </>
                )}
            </Paper>

            <Dialog
                open={openDeleteDialog}
                onClose={() => setOpenDeleteDialog(false)}
            >
                <DialogTitle>Xác nhận xóa</DialogTitle>
                <DialogContent>
                    Bạn có chắc chắn muốn xóa {selectedLogs.length} bản ghi đã chọn?
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenDeleteDialog(false)}>Hủy</Button>
                    <Button onClick={handleDeleteLogs} color="error">Xóa</Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default AssetTransferHistory;
