import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    IconButton,
    Tooltip,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';
import {
    Add as AddIcon,
    Edit as EditIcon,
    Delete as DeleteIcon,
} from '@mui/icons-material';
import axios from 'axios';

const OfficeManagement = () => {
    const [offices, setOffices] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [teams, setTeams] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedOffice, setSelectedOffice] = useState(null);
    const [formData, setFormData] = useState({
        code: '',
        label: '',
        departmentID: '',
        teamID: '',
    });

    useEffect(() => {
        fetchOffices();
        fetchDepartments();
        fetchTeams();
    }, []);

    const fetchOffices = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/office');
            setOffices(response.data);
        } catch (error) {
            console.error('Error fetching offices:', error);
        }
    };

    const fetchDepartments = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/departmant');
            setDepartments(response.data);
        } catch (error) {
            console.error('Error fetching departments:', error);
        }
    };

    const fetchTeams = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/team');
            setTeams(response.data);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    const handleOpenDialog = (office = null) => {
        if (office) {
            setSelectedOffice(office);
            setFormData({
                code: office.code,
                label: office.label,
                departmentID: office.departmentID,
                teamID: office.teamID,
            });
        } else {
            setSelectedOffice(null);
            setFormData({
                code: '',
                label: '',
                departmentID: '',
                teamID: '',
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedOffice(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            if (selectedOffice) {
                await axios.put(`http://localhost:3076/api/vanminh/office/${selectedOffice._id}`, formData);
            } else {
                await axios.post('http://localhost:3076/api/vanminh/office', formData);
            }
            handleCloseDialog();
            fetchOffices();
        } catch (error) {
            console.error('Error saving office:', error);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa?')) {
            try {
                await axios.delete(`http://localhost:3076/api/vanminh/office/${id}`);
                fetchOffices();
            } catch (error) {
                console.error('Error deleting office:', error);
            }
        }
    };

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý văn phòng</Typography>
                <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => handleOpenDialog()}
                >
                    Thêm mới
                </Button>
            </Box>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Mã văn phòng</TableCell>
                            <TableCell>Tên văn phòng</TableCell>
                            <TableCell>Phòng ban</TableCell>
                            <TableCell>Team</TableCell>
                            <TableCell>Thao tác</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {offices.map((office) => (
                            <TableRow key={office._id}>
                                <TableCell>{office.code}</TableCell>
                                <TableCell>{office.label}</TableCell>
                                <TableCell>
                                    {departments.find(d => d._id === office.departmentID)?.label}
                                </TableCell>
                                <TableCell>
                                    {teams.find(t => t._id === office.teamID)?.label}
                                </TableCell>
                                <TableCell>
                                    <Tooltip title="Sửa">
                                        <IconButton onClick={() => handleOpenDialog(office)}>
                                            <EditIcon />
                                        </IconButton>
                                    </Tooltip>
                                    <Tooltip title="Xóa">
                                        <IconButton onClick={() => handleDelete(office._id)}>
                                            <DeleteIcon />
                                        </IconButton>
                                    </Tooltip>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
                <DialogTitle>
                    {selectedOffice ? 'Cập nhật văn phòng' : 'Thêm mới văn phòng'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Mã văn phòng"
                            name="code"
                            value={formData.code}
                            onChange={handleInputChange}
                            fullWidth
                            required
                        />
                        <TextField
                            label="Tên văn phòng"
                            name="label"
                            value={formData.label}
                            onChange={handleInputChange}
                            fullWidth
                            required
                        />
                        <FormControl fullWidth>
                            <InputLabel>Phòng ban</InputLabel>
                            <Select
                                name="departmentID"
                                value={formData.departmentID}
                                onChange={handleInputChange}
                                label="Phòng ban"
                                required
                            >
                                {departments.map((department) => (
                                    <MenuItem key={department._id} value={department._id}>
                                        {department.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <FormControl fullWidth>
                            <InputLabel>Team</InputLabel>
                            <Select
                                name="teamID"
                                value={formData.teamID}
                                onChange={handleInputChange}
                                label="Team"
                                required
                            >
                                {teams.map((team) => (
                                    <MenuItem key={team._id} value={team._id}>
                                        {team.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedOffice ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default OfficeManagement;
