import React, { useState, useEffect, useCallback } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Alert,
    Snackbar,
    TableContainer,
    Tooltip,
    Grid,
    FormHelperText,
    ListSubheader
} from '@mui/material';
import {
    Add as AddIcon,
    ImportExport as ImportExportIcon
} from '@mui/icons-material';
import DataTable from '../components/DataTable';
import axios from 'axios';
import assetTransferApi from '../api/assetTransferApi';
import officeApi from '../api/officeApi';
import axiosClient from '../api/axiosClient';
import { jwtDecode } from 'jwt-decode';

const AssetManagement = () => {
    const [assets, setAssets] = useState([]);
    const [units, setUnits] = useState([]);
    const [assetTypes, setAssetTypes] = useState([]);
    const [offices, setOffices] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedAsset, setSelectedAsset] = useState(null);
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'success'
    });
    const [formData, setFormData] = useState({
        code: '',
        quantity: '',
        unit: '',
        name: '',
        modelOrSeries: '',
        assetType: '',
        dateOfPurchase: '',
        warranty: '',
        expirationDate: '',
        price: '',
        depreciation: '',
        supplier: '',
        addressNCC: '',
        phoneNCC: '',
        assetLocation: '',
        managementOffice: '',
        description: '',
        seeMore: {},
    });
    const [pagination, setPagination] = useState({
        page: 1,
        limit: 10,
        total: 0,
    });
    const [transferDialog, setTransferDialog] = useState(false);
    const [transferType, setTransferType] = useState('IMPORT');
    const [transferData, setTransferData] = useState({
        assetId: '',
        quantity: 0,
        fromOfficeId: '',
        toOfficeId: '',
        note: '',
        reason: 'NEW_IMPORT'
    });

    useEffect(() => {
        fetchAssets();
        fetchUnits();
        fetchAssetTypes();
        fetchOffices();
        fetchDepartments();
    }, []);

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        return {
            headers: {
                'Authorization': token
            }
        };
    };

    const getUserIdFromToken = () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('No token found in localStorage');
            return null;
        }

        try {
            const decoded = jwtDecode(token);
            console.log('Decoded token:', decoded);

            const userId = decoded.id || decoded._id;
            console.log('Extracted userId:', userId);

            if (!userId) {
                console.warn('No id found in decoded token');
                return null;
            }

            return userId;
        } catch (error) {
            console.error('Error decoding token:', error);
            return null;
        }
    };

    const fetchAssets = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/asset', getAuthConfig());
            setAssets(response.data.data || []);
            setPagination(prev => ({
                ...prev,
                total: response.data.total
            }));
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu tài sản', 'error');
        }
    };

    const fetchUnits = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/unit', getAuthConfig());
            setUnits(response.data || []);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu đơn vị', 'error');
        }
    };

    const fetchAssetTypes = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/asset-type', getAuthConfig());
            setAssetTypes(response.data || []);
        } catch (error) {
            showSnackbar('Lỗi khi tải dữ liệu loại tài sản', 'error');
        }
    };

    const fetchOffices = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/office', getAuthConfig());
            console.log('Offices API Response:', response);

            if (response.data && Array.isArray(response.data)) {
                console.log('Setting offices:', response.data);
                setOffices(response.data);
            } else {
                console.error('Invalid offices data format:', response.data);
                showSnackbar('Dữ liệu văn phòng không đúng định dạng', 'error');
            }
        } catch (error) {
            console.error('Error fetching offices:', error);
            showSnackbar('Lỗi khi tải danh sách văn phòng', 'error');
        }
    };

    const fetchDepartments = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/departmant', getAuthConfig());
            console.log('Departments API Response:', response);

            if (response.data && Array.isArray(response.data)) {
                console.log('Setting departments:', response.data);
                setDepartments(response.data);
            } else {
                console.error('Invalid departments data format:', response.data);
                showSnackbar('Dữ liệu phòng ban không đúng định dạng', 'error');
            }
        } catch (error) {
            console.error('Error fetching departments:', error);
            showSnackbar('Lỗi khi tải dữ liệu phòng ban', 'error');
        }
    };

    const handleOpenDialog = (asset = null) => {
        setSelectedAsset(asset);
        if (asset) {
            setFormData({
                name: asset.name,
                code: asset.code,
                unit: asset.unit?._id || '',
                assetType: asset.assetType?._id || '',
                assetLocation: asset.assetLocation?._id || '',
                managementOffice: asset.managementOffice?._id || '',
                quantity: asset.quantity,
                status: asset.status,
                description: asset.description || '',
            });
        } else {
            setFormData({
                name: '',
                code: '',
                unit: '',
                assetType: '',
                assetLocation: '',
                managementOffice: '',
                quantity: 1,
                status: 'active',
                description: '',
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedAsset(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const submitData = {
                ...formData,
                unit: formData.unit || null,
                assetType: formData.assetType || null,
                assetLocation: formData.assetLocation || null,
                managementOffice: formData.managementOffice || null,
            };

            if (selectedAsset) {
                await axios.put(`http://localhost:3076/api/vanminh/asset/${selectedAsset._id}`, submitData, getAuthConfig());
                showSnackbar('Cập nhật tài sản thành công');
            } else {
                await axios.post('http://localhost:3076/api/vanminh/asset', submitData, getAuthConfig());
                showSnackbar('Thêm tài sản thành công');
            }
            handleCloseDialog();
            fetchAssets();
        } catch (error) {
            showSnackbar('Có lỗi xảy ra', 'error');
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3076/api/vanminh/asset/${id}`, getAuthConfig());
            showSnackbar('Xóa tài sản thành công');
            fetchAssets();
        } catch (error) {
            showSnackbar('Lỗi khi xóa tài sản', 'error');
        }
    };

    const handleCloseSnackbar = () => {
        setSnackbar(prev => ({ ...prev, open: false }));
    };

    const showSnackbar = (message, severity = 'success') => {
        setSnackbar({ open: true, message, severity });
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        return new Date(dateString).toLocaleDateString();
    };

    const formatCurrency = (value) => {
        if (!value) return '';
        return value.toLocaleString('vi-VN') + ' VNĐ';
    };

    const formatPrice = (price) => {
        return new Intl.NumberFormat('vi-VN', { 
            style: 'currency', 
            currency: 'VND' 
        }).format(price);
    };

    const columns = [
        {
            field: 'code',
            headerName: 'Mã tài sản',
            width: 150
        },
        {
            field: 'name',
            headerName: 'Tên tài sản',
            width: 200
        },
        {
            field: 'quantity',
            headerName: 'Số lượng',
            width: 120,
            renderCell: (row) => `${row.quantity || 0} ${row.unit?.label || ''}`
        },
        {
            field: 'assetType',
            headerName: 'Loại tài sản',
            width: 150,
            renderCell: (row) => row.assetType?.label || 'Chưa phân loại'
        },
        {
            field: 'managementOffice',
            headerName: 'Văn phòng quản lý',
            width: 200,
            renderCell: (row) => row.managementOffice?.label || 'Chưa xác định'
        },
        {
            field: 'price',
            headerName: 'Giá trị',
            width: 150,
            renderCell: (row) => formatPrice(row.price || 0)
        },
        {
            field: 'actions',
            headerName: 'Thao tác',
            width: 300,
            renderCell: (row) => (
                <Box>
                    <Button
                        variant="contained"
                        color="primary"
                        size="small"
                        onClick={() => handleOpenDialog(row)}
                        sx={{ mr: 1 }}
                    >
                        Chi tiết
                    </Button>
                    <Button
                        variant="contained"
                        color="success"
                        size="small"
                        onClick={() => handleImportAsset(row)}
                        sx={{ mr: 1 }}
                    >
                        Nhập kho
                    </Button>
                    <Button
                        variant="contained"
                        color="secondary"
                        size="small"
                        onClick={() => handleTransferDialogOpen(row)}
                    >
                        Xuất kho
                    </Button>
                </Box>
            )
        }
    ];

    const handlePageChange = (page) => {
        setPagination(prev => ({ ...prev, page }));
        fetchAssets();
    };

    const handleLimitChange = (limit) => {
        setPagination(prev => ({ ...prev, limit }));
        fetchAssets();
    };

    const handleTransferDialogOpen = (asset) => {
        if (!asset || !asset._id) {
            showSnackbar('Không thể xuất kho: Thiếu thông tin tài sản', 'error');
            return;
        }

        setTransferType('EXPORT');
        setTransferData({
            assetId: asset._id,
            quantity: asset.quantity || 1,
            fromOfficeId: asset.managementOffice?._id || '',
            toOfficeId: '',
            note: '',
            reason: 'TRANSFER'
        });
        setTransferDialog(true);
    };

    const handleTransferDialogClose = () => {
        setTransferDialog(false);
        setTransferData({
            assetId: '',
            quantity: 0,
            fromOfficeId: '',
            toOfficeId: '',
            note: '',
            reason: 'NEW_IMPORT'
        });
    };

    const handleTransferChange = (event) => {
        const { name, value } = event.target;
        setTransferData(prev => ({
            ...prev,
            [name]: value
        }));
        console.log('Transfer data updated:', { name, value });
    };

    const handleTransferSubmit = async () => {
        try {
            // Kiểm tra dữ liệu đầu vào
            if (!transferData.assetId) {
                showSnackbar('Vui lòng chọn tài sản', 'error');
                return;
            }

            if (!transferData.quantity || transferData.quantity <= 0) {
                showSnackbar('Số lượng phải lớn hơn 0', 'error');
                return;
            }

            const userId = getUserIdFromToken();
            if (!userId) {
                showSnackbar('Không tìm thấy thông tin người dùng', 'error');
                return;
            }

            if (transferType === 'EXPORT') {
                if (!transferData.fromOfficeId) {
                    showSnackbar('Vui lòng chọn văn phòng nguồn', 'error');
                    return;
                }
                if (!transferData.toOfficeId) {
                    showSnackbar('Vui lòng chọn văn phòng đích', 'error');
                    return;
                }
                if (transferData.fromOfficeId === transferData.toOfficeId) {
                    showSnackbar('Văn phòng nguồn và đích không được trùng nhau', 'error');
                    return;
                }
            }

            const requestData = {
                ...transferData,
                transferType,
                userId
            };
            console.log('Transfer request data:', requestData);

            let response;
            if (transferType === 'IMPORT') {
                response = await assetTransferApi.importAsset(requestData);
            } else {
                response = await assetTransferApi.exportAsset(requestData);
            }

            showSnackbar(response?.message || 'Thao tác thành công', 'success');
            handleTransferDialogClose();
            fetchAssets();

        } catch (error) {
            console.error('Error in transfer:', error);
            showSnackbar(error.response?.data?.message || 'Có lỗi xảy ra', 'error');
        }
    };

    const handleImportAsset = async (asset) => {
        try {
            const userId = getUserIdFromToken();
            if (!userId) {
                showSnackbar('Không tìm thấy thông tin người dùng', 'error');
                return;
            }

            // Mở dialog nhập kho
            setTransferType('IMPORT');
            setTransferData({
                assetId: asset._id,
                quantity: 1,
                fromOfficeId: asset.managementOffice?._id || '',
                toOfficeId: asset.managementOffice?._id || '',
                note: '',
                reason: 'NEW_IMPORT',
                userId: userId
            });
            setTransferDialog(true);
        } catch (error) {
            console.error('Error in import asset:', error);
            showSnackbar(error.response?.data?.message || 'Có lỗi xảy ra khi nhập kho', 'error');
        }
    };

    const renderTransferDialog = () => {
        const selectedAsset = assets.find(a => a._id === transferData.assetId);
        const availableOffices = offices.filter(office => office._id !== transferData.fromOfficeId);

        return (
            <Dialog open={transferDialog} onClose={handleTransferDialogClose} maxWidth="md" fullWidth>
                <DialogTitle>
                    {transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho'}
                </DialogTitle>
                <DialogContent>
                    <Grid container spacing={2} sx={{ mt: 1 }}>
                        {selectedAsset && (
                            <Grid item xs={12}>
                                <Paper sx={{ p: 2, mb: 2, bgcolor: 'grey.50' }}>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Tên tài sản:</strong> {selectedAsset.name}
                                    </Typography>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Mã tài sản:</strong> {selectedAsset.code}
                                    </Typography>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Loại tài sản:</strong> {selectedAsset.assetType?.label || 'Chưa phân loại'}
                                    </Typography>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Số lượng hiện có:</strong> {selectedAsset.quantity} {selectedAsset.unit?.label || 'đơn vị'}
                                    </Typography>
                                    <Typography variant="subtitle1" gutterBottom>
                                        <strong>Văn phòng quản lý:</strong> {selectedAsset.managementOffice?.label || 'Chưa xác định'}
                                    </Typography>
                                </Paper>
                            </Grid>
                        )}

                        {transferType === 'EXPORT' && (
                            <>
                                <Grid item xs={6}>
                                    <TextField
                                        fullWidth
                                        label="Văn phòng nguồn"
                                        value={selectedAsset?.managementOffice?.label || ''}
                                        disabled
                                    />
                                </Grid>
                                <Grid item xs={6}>
                                    <FormControl fullWidth required error={!transferData.toOfficeId}>
                                        <InputLabel>Văn phòng đích</InputLabel>
                                        <Select
                                            name="toOfficeId"
                                            value={transferData.toOfficeId}
                                            onChange={handleTransferChange}
                                            label="Văn phòng đích"
                                        >
                                            {availableOffices.map((office) => (
                                                <MenuItem key={office._id} value={office._id}>
                                                    {office.label}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                        {!transferData.toOfficeId && (
                                            <FormHelperText>Vui lòng chọn văn phòng đích</FormHelperText>
                                        )}
                                    </FormControl>
                                </Grid>
                            </>
                        )}

                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                type="number"
                                label="Số lượng"
                                name="quantity"
                                value={transferData.quantity}
                                onChange={handleTransferChange}
                                required
                                error={transferData.quantity <= 0}
                                helperText={transferData.quantity <= 0 ? "Số lượng phải lớn hơn 0" : ""}
                                InputProps={{
                                    inputProps: { min: 1 }
                                }}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <FormControl fullWidth>
                                <InputLabel>Lý do</InputLabel>
                                <Select
                                    name="reason"
                                    value={transferData.reason}
                                    onChange={(e) => {
                                        const value = e.target.value;
                                        console.log('Selected reason:', value);
                                        setTransferData(prev => ({
                                            ...prev,
                                            reason: value
                                        }));
                                    }}
                                >
                                    <MenuItem value="TRANSFER">Điều chuyển</MenuItem>
                                    <MenuItem value="RETURN">Trả về</MenuItem>
                                    <MenuItem value="MAINTENANCE">Bảo trì</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                multiline
                                rows={3}
                                label="Ghi chú"
                                name="note"
                                value={transferData.note}
                                onChange={handleTransferChange}
                                placeholder="Nhập ghi chú về việc nhập/xuất kho (nếu có)"
                            />
                        </Grid>
                    </Grid>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleTransferDialogClose}>Hủy</Button>
                    <Button 
                        onClick={handleTransferSubmit}
                        variant="contained"
                        color="primary"
                        disabled={
                            !transferData.assetId ||
                            !transferData.quantity ||
                            transferData.quantity <= 0 ||
                            !transferData.reason ||
                            (transferType === 'EXPORT' && !transferData.toOfficeId)
                        }
                    >
                        {transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho'}
                    </Button>
                </DialogActions>
            </Dialog>
        );
    };

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý tài sản</Typography>
                <Box>
                    <Button
                        variant="contained"
                        color="secondary"
                        startIcon={<ImportExportIcon />}
                        onClick={() => handleTransferDialogOpen(null, 'IMPORT')}
                        sx={{ mr: 1 }}
                    >
                        Nhập kho
                    </Button>
                    <Button
                        variant="contained"
                        color="secondary"
                        startIcon={<ImportExportIcon />}
                        onClick={() => handleTransferDialogOpen(null, 'EXPORT')}
                        sx={{ mr: 1 }}
                    >
                        Xuất kho
                    </Button>
                    <Button
                        variant="contained"
                        startIcon={<AddIcon />}
                        onClick={() => handleOpenDialog()}
                    >
                        Thêm mới
                    </Button>
                </Box>
            </Box>

            <Paper 
                sx={{ 
                    mb: 2, 
                    overflow: 'hidden',
                    borderRadius: 2,
                    boxShadow: 3,
                    '& .MuiTableRow-root:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                    },
                    '& .MuiTableCell-head': {
                        backgroundColor: '#FFA726',
                        color: '#fff',
                        fontWeight: 'bold',
                        fontSize: '0.9rem',
                        padding: '12px 16px',
                        textTransform: 'uppercase',
                        letterSpacing: '0.5px',
                        borderBottom: 'none',
                        whiteSpace: 'nowrap'
                    },
                    '& .MuiTableCell-root': {
                        borderBottom: '1px solid rgba(224, 224, 224, 1)',
                        padding: '8px 16px',
                        fontSize: '0.875rem'
                    },
                    '& .MuiTablePagination-root': {
                        backgroundColor: '#f5f5f5',
                        borderTop: '1px solid rgba(224, 224, 224, 1)'
                    }
                }}
            >
                <TableContainer>
                    <DataTable
                        columns={columns}
                        data={assets}
                        onEdit={handleOpenDialog}
                        onDelete={handleDelete}
                        pagination={{
                            page: pagination.page,
                            limit: pagination.limit,
                            total: pagination.total,
                            onPageChange: handlePageChange,
                            onLimitChange: handleLimitChange
                        }}
                    />
                </TableContainer>
            </Paper>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                <DialogTitle>
                    {selectedAsset ? 'Cập nhật tài sản' : 'Thêm mới tài sản'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Mã tài sản"
                            name="code"
                            value={formData.code}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Tên tài sản"
                            name="name"
                            value={formData.name}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Model/Series"
                            name="modelOrSeries"
                            value={formData.modelOrSeries}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <FormControl fullWidth>
                            <InputLabel>Loại tài sản</InputLabel>
                            <Select
                                name="assetType"
                                value={formData.assetType}
                                onChange={handleInputChange}
                                label="Loại tài sản"
                            >
                                {assetTypes.map((type) => (
                                    <MenuItem key={type._id} value={type._id}>
                                        {type.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <Box sx={{ display: 'flex', gap: 2 }}>
                            <TextField
                                label="Số lượng"
                                name="quantity"
                                type="number"
                                value={formData.quantity}
                                onChange={handleInputChange}
                                fullWidth
                            />
                            <FormControl fullWidth>
                                <InputLabel>Đơn vị</InputLabel>
                                <Select
                                    name="unit"
                                    value={formData.unit}
                                    onChange={handleInputChange}
                                    label="Đơn vị"
                                >
                                    {units.map((unit) => (
                                        <MenuItem key={unit._id} value={unit._id}>
                                            {unit.label}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Box>
                        <TextField
                            label="Giá"
                            name="price"
                            type="number"
                            value={formData.price}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Ngày mua"
                            name="dateOfPurchase"
                            type="date"
                            value={formData.dateOfPurchase}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Thời hạn bảo hành (tháng)"
                            name="warranty"
                            type="number"
                            value={formData.warranty}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Ngày hết hạn"
                            name="expirationDate"
                            type="date"
                            value={formData.expirationDate}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Khấu hao"
                            name="depreciation"
                            value={formData.depreciation}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Nhà cung cấp"
                            name="supplier"
                            value={formData.supplier}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Địa chỉ nhà cung cấp"
                            name="addressNCC"
                            value={formData.addressNCC}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <TextField
                            label="Số điện thoại nhà cung cấp"
                            name="phoneNCC"
                            value={formData.phoneNCC}
                            onChange={handleInputChange}
                            fullWidth
                        />
                        <FormControl fullWidth>
                            <InputLabel>Vị trí tài sản</InputLabel>
                            <Select
                                name="assetLocation"
                                value={formData.assetLocation}
                                onChange={handleInputChange}
                                label="Vị trí tài sản"
                            >
                                {offices.map((office) => (
                                    <MenuItem key={office._id} value={office._id}>
                                        {office.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <FormControl fullWidth>
                            <InputLabel>Văn phòng quản lý</InputLabel>
                            <Select
                                name="managementOffice"
                                value={formData.managementOffice}
                                onChange={handleInputChange}
                                label="Văn phòng quản lý"
                            >
                                {offices.map((office) => (
                                    <MenuItem key={office._id} value={office._id}>
                                        {office.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <TextField
                            label="Mô tả"
                            name="description"
                            value={formData.description}
                            onChange={handleInputChange}
                            fullWidth
                            multiline
                            rows={3}
                        />
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedAsset ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>

            {renderTransferDialog()}

            <Snackbar
                open={snackbar.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert
                    onClose={handleCloseSnackbar}
                    severity={snackbar.severity}
                    sx={{ width: '100%' }}
                >
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default AssetManagement;
