import React, { useState, useEffect } from 'react';
import {
    Box,
    Grid,
    Paper,
    Typography,
    CircularProgress,
    Card,
    CardContent,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow
} from '@mui/material';
import {
    Inventory as InventoryIcon,
    DirectionsCar as CarIcon,
    Business as OfficeIcon,
    People as PeopleIcon,
    Category as CategoryIcon,
    TrendingUp as TrendingUpIcon
} from '@mui/icons-material';
import assetApi from '../api/assetApi';
import carApi from '../api/carApi';
import officeApi from '../api/officeApi';
import userApi from '../api/userApi';
import assetTypeApi from '../api/assetTypeApi';
import assetTransferApi from '../api/assetTransferApi';

const StatCard = ({ title, value, icon: Icon, color, subtitle }) => (
    <Card sx={{ height: '100%' }}>
        <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Icon sx={{ fontSize: 40, color: color, mr: 2 }} />
                <Box>
                    <Typography variant="h6" component="div">
                        {title}
                    </Typography>
                    {subtitle && (
                        <Typography variant="body2" color="text.secondary">
                            {subtitle}
                        </Typography>
                    )}
                </Box>
            </Box>
            <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
                {value}
            </Typography>
        </CardContent>
    </Card>
);

const Dashboard = () => {
    const [stats, setStats] = useState({
        totalAssets: 0,
        totalCars: 0,
        totalOffices: 0,
        totalUsers: 0,
        totalAssetTypes: 0,
        recentTransfers: [],
        assetByType: [],
        assetByOffice: []
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchStats = async () => {
            try {
                setLoading(true);
                const [
                    assetsResponse,
                    carsResponse,
                    officesResponse,
                    usersResponse,
                    assetTypesResponse,
                    transfersResponse
                ] = await Promise.all([
                    assetApi.getAll(),
                    carApi.getAll(),
                    officeApi.getAll(),
                    userApi.getAll(),
                    assetTypeApi.getAll(),
                    assetTransferApi.getLogs({ limit: 5 })
                ]);

                // Tính toán số lượng tài sản theo loại
                const assetByType = {};
                assetsResponse?.data?.forEach(asset => {
                    const type = asset.assetType?.label || 'Chưa phân loại';
                    assetByType[type] = (assetByType[type] || 0) + 1;
                });

                // Tính toán số lượng tài sản theo văn phòng
                const assetByOffice = {};
                assetsResponse?.data?.forEach(asset => {
                    const office = asset.managementOffice?.label || 'Chưa xác định';
                    assetByOffice[office] = (assetByOffice[office] || 0) + 1;
                });

                setStats({
                    totalAssets: assetsResponse?.total || 0,
                    totalCars: carsResponse?.total || 0,
                    totalOffices: officesResponse?.length || 0,
                    totalUsers: usersResponse?.total || 0,
                    totalAssetTypes: assetTypesResponse?.length || 0,
                    recentTransfers: transfersResponse?.logs || [],
                    assetByType: Object.entries(assetByType).map(([type, count]) => ({ type, count })),
                    assetByOffice: Object.entries(assetByOffice).map(([office, count]) => ({ office, count }))
                });
            } catch (error) {
                console.error('Error fetching stats:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchStats();
    }, []);

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" sx={{ mb: 4 }}>
                Tổng quan hệ thống
            </Typography>

            <Grid container spacing={3}>
                <Grid item xs={12} sm={6} md={4} lg={2.4}>
                    <StatCard
                        title="Tổng tài sản"
                        value={stats.totalAssets}
                        icon={InventoryIcon}
                        color="#2196f3"
                        subtitle={`${stats.assetByType.length} loại tài sản`}
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={4} lg={2.4}>
                    <StatCard
                        title="Tổng xe"
                        value={stats.totalCars}
                        icon={CarIcon}
                        color="#4caf50"
                        subtitle="Đang hoạt động"
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={4} lg={2.4}>
                    <StatCard
                        title="Tổng văn phòng"
                        value={stats.totalOffices}
                        icon={OfficeIcon}
                        color="#ff9800"
                        subtitle="Đang hoạt động"
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={4} lg={2.4}>
                    <StatCard
                        title="Tổng người dùng"
                        value={stats.totalUsers}
                        icon={PeopleIcon}
                        color="#9c27b0"
                        subtitle="Đang hoạt động"
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={4} lg={2.4}>
                    <StatCard
                        title="Loại tài sản"
                        value={stats.totalAssetTypes}
                        icon={CategoryIcon}
                        color="#f44336"
                        subtitle="Đang sử dụng"
                    />
                </Grid>

                {/* Bảng tài sản theo loại */}
                <Grid item xs={12} md={6}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Tài sản theo loại
                        </Typography>
                        <TableContainer>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Loại tài sản</TableCell>
                                        <TableCell align="right">Số lượng</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {stats.assetByType.map((item) => (
                                        <TableRow key={item.type}>
                                            <TableCell>{item.type}</TableCell>
                                            <TableCell align="right">{item.count}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Paper>
                </Grid>

                {/* Bảng tài sản theo văn phòng */}
                <Grid item xs={12} md={6}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Tài sản theo văn phòng
                        </Typography>
                        <TableContainer>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Văn phòng</TableCell>
                                        <TableCell align="right">Số lượng</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {stats.assetByOffice.map((item) => (
                                        <TableRow key={item.office}>
                                            <TableCell>{item.office}</TableCell>
                                            <TableCell align="right">{item.count}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Paper>
                </Grid>

                {/* Lịch sử gần đây */}
                <Grid item xs={12}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h6" sx={{ mb: 2 }}>
                            Lịch sử điều chuyển gần đây
                        </Typography>
                        <TableContainer>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Tài sản</TableCell>
                                        <TableCell>Loại</TableCell>
                                        <TableCell>Số lượng</TableCell>
                                        <TableCell>Từ</TableCell>
                                        <TableCell>Đến</TableCell>
                                        <TableCell>Ngày</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {stats.recentTransfers.map((transfer) => (
                                        <TableRow key={transfer._id}>
                                            <TableCell>{transfer.assetName}</TableCell>
                                            <TableCell>
                                                {transfer.transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho'}
                                            </TableCell>
                                            <TableCell>{transfer.quantity}</TableCell>
                                            <TableCell>{transfer.fromOffice}</TableCell>
                                            <TableCell>{transfer.toOffice}</TableCell>
                                            <TableCell>{transfer.transferDate}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Paper>
                </Grid>
            </Grid>
        </Box>
    );
};

export default Dashboard;
