import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    Paper,
    Alert,
    Snackbar,
    MenuItem,
    Grid,
    IconButton,
    Tooltip
} from '@mui/material';
import {
    Add as AddIcon,
    Edit as EditIcon,
    Delete as DeleteIcon,
    TransferWithinAStation as TransferIcon
} from '@mui/icons-material';
import DataTable from '../components/DataTable';
import axiosClient from '../api/axiosClient';

const AssetManagement = () => {
    const [assets, setAssets] = useState([]);
    const [units, setUnits] = useState([]);
    const [assetTypes, setAssetTypes] = useState([]);
    const [offices, setOffices] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [openTransferDialog, setOpenTransferDialog] = useState(false);
    const [selectedAsset, setSelectedAsset] = useState(null);
    const [formData, setFormData] = useState({
        code: '',
        name: '',
        quantity: '',
        unit: '',
        modelOrSeries: '',
        assetType: '',
        dateOfPurchase: '',
        warranty: '',
        expirationDate: '',
        price: '',
        depreciation: '',
        supplier: '',
        addressNCC: '',
        phoneNCC: '',
        assetLocation: '',
        managementOffice: '',
        description: ''
    });
    const [transferData, setTransferData] = useState({
        assetId: '',
        fromOfficeId: '',
        toOfficeId: '',
        quantity: '',
        note: '',
        transferType: 'EXPORT'
    });
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'success'
    });
    const [pagination, setPagination] = useState({
        page: 0,
        limit: 10,
        total: 0
    });

    useEffect(() => {
        fetchAssets();
        fetchUnits();
        fetchAssetTypes();
        fetchOffices();
        fetchDepartments();
    }, []);

    useEffect(() => {
        fetchAssets();
    }, [pagination.page, pagination.limit]);

    const fetchAssets = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('Không tìm thấy token!');
                return;
            }

            console.log('Token:', token);
            const response = await axiosClient.get('/asset', {
                params: {
                    page: pagination.page + 1,
                    limit: pagination.limit
                }
            });

            console.log('Raw API response:', response);

            if (response && response.data) {
                console.log('Assets data:', response.data);
                const formattedData = Array.isArray(response.data) ? response.data : [];
                setAssets(formattedData);
                setPagination(prev => ({
                    ...prev,
                    total: response.total || 0
                }));
            } else {
                console.warn('Không có dữ liệu hoặc dữ liệu không đúng format:', response);
                setAssets([]);
            }
        } catch (error) {
            console.error('Lỗi khi lấy dữ liệu tài sản:', error);
            showSnackbar('Lỗi khi lấy dữ liệu tài sản: ' + (error.response?.data?.message || error.message), 'error');
            setAssets([]);
        }
    };

    const fetchUnits = async () => {
        try {
            const response = await axiosClient.get('/unit');
            console.log('Units response:', response);
            setUnits(response.data || []);
        } catch (error) {
            console.error('Lỗi khi lấy dữ liệu đơn vị:', error);
        }
    };

    const fetchAssetTypes = async () => {
        try {
            const response = await axiosClient.get('/asset-type');
            console.log('Asset types response:', response);
            setAssetTypes(response.data || []);
        } catch (error) {
            console.error('Lỗi khi lấy dữ liệu loại tài sản:', error);
        }
    };

    const fetchOffices = async () => {
        try {
            const response = await axiosClient.get('/office');
            console.log('Offices response:', response);
            setOffices(response.data || []);
        } catch (error) {
            console.error('Lỗi khi lấy dữ liệu văn phòng:', error);
        }
    };

    const fetchDepartments = async () => {
        try {
            const response = await axiosClient.get('/departmant');
            console.log('Departments response:', response);
            setDepartments(response.data || []);
        } catch (error) {
            console.error('Lỗi khi lấy dữ liệu phòng ban:', error);
        }
    };

    const handleOpenDialog = (asset = null) => {
        if (asset) {
            setSelectedAsset(asset);
            setFormData({
                code: asset.code || '',
                name: asset.name || '',
                quantity: asset.quantity || '',
                unit: asset.unit?._id || '',
                modelOrSeries: asset.modelOrSeries || '',
                assetType: asset.assetType?._id || '',
                dateOfPurchase: asset.dateOfPurchase || '',
                warranty: asset.warranty || '',
                expirationDate: asset.expirationDate || '',
                price: asset.price || '',
                depreciation: asset.depreciation || '',
                supplier: asset.supplier || '',
                addressNCC: asset.addressNCC || '',
                phoneNCC: asset.phoneNCC || '',
                assetLocation: asset.assetLocation?._id || '',
                managementOffice: asset.managementOffice?._id || '',
                description: asset.description || ''
            });
        } else {
            setSelectedAsset(null);
            setFormData({
                code: '',
                name: '',
                quantity: '',
                unit: '',
                modelOrSeries: '',
                assetType: '',
                dateOfPurchase: '',
                warranty: '',
                expirationDate: '',
                price: '',
                depreciation: '',
                supplier: '',
                addressNCC: '',
                phoneNCC: '',
                assetLocation: '',
                managementOffice: '',
                description: ''
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedAsset(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            const submitData = {
                ...formData,
                unit: formData.unit || null,
                assetType: formData.assetType || null,
                assetLocation: formData.assetLocation || null,
                managementOffice: formData.managementOffice || null
            };

            if (selectedAsset) {
                await axiosClient.put(`/asset/${selectedAsset._id}`, submitData);
                showSnackbar('Cập nhật tài sản thành công');
            } else {
                await axiosClient.post('/asset', submitData);
                showSnackbar('Thêm tài sản thành công');
            }
            handleCloseDialog();
            fetchAssets();
        } catch (error) {
            console.error('Lỗi khi lưu tài sản:', error);
            showSnackbar(error.response?.data?.message || 'Có lỗi xảy ra', 'error');
        }
    };

    const handleDelete = async (id) => {
        try {
            await axiosClient.delete(`/asset/${id}`);
            showSnackbar('Xóa tài sản thành công');
            fetchAssets();
        } catch (error) {
            console.error('Lỗi khi xóa tài sản:', error);
            showSnackbar(error.response?.data?.message || 'Có lỗi xảy ra', 'error');
        }
    };

    const handleCloseSnackbar = () => {
        setSnackbar(prev => ({ ...prev, open: false }));
    };

    const showSnackbar = (message, severity = 'success') => {
        setSnackbar({ open: true, message, severity });
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        return new Date(dateString).toLocaleDateString('vi-VN');
    };

    const formatCurrency = (value) => {
        if (!value) return '';
        return new Intl.NumberFormat('vi-VN', {
            style: 'currency',
            currency: 'VND'
        }).format(value);
    };

    const formatPrice = (price) => {
        if (!price) return '';
        return new Intl.NumberFormat('vi-VN', {
            style: 'currency',
            currency: 'VND'
        }).format(price);
    };

    const handlePageChange = (page) => {
        setPagination(prev => ({ ...prev, page }));
    };

    const handleLimitChange = (limit) => {
        setPagination(prev => ({ ...prev, limit, page: 0 }));
    };

    const handleTransferDialogOpen = (asset) => {
        setSelectedAsset(asset);
        setTransferData({
            assetId: asset._id,
            fromOfficeId: asset.managementOffice?._id || '',
            toOfficeId: '',
            quantity: '',
            note: '',
            transferType: 'EXPORT'
        });
        setOpenTransferDialog(true);
    };

    const handleTransferDialogClose = () => {
        setOpenTransferDialog(false);
        setSelectedAsset(null);
        setTransferData({
            assetId: '',
            fromOfficeId: '',
            toOfficeId: '',
            quantity: '',
            note: '',
            transferType: 'EXPORT'
        });
    };

    const handleTransferChange = (event) => {
        const { name, value } = event.target;
        setTransferData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleTransferSubmit = async () => {
        try {
            if (!transferData.quantity) {
                showSnackbar('Vui lòng nhập số lượng', 'error');
                return;
            }

            if (transferData.transferType === 'EXPORT') {
                if (!transferData.fromOfficeId) {
                    showSnackbar('Vui lòng chọn văn phòng nguồn', 'error');
                    return;
                }
                if (!transferData.toOfficeId) {
                    showSnackbar('Vui lòng chọn văn phòng đích', 'error');
                    return;
                }
                if (transferData.fromOfficeId === transferData.toOfficeId) {
                    showSnackbar('Văn phòng nguồn và đích không được trùng nhau', 'error');
                    return;
                }
            }

            let response;
            if (transferData.transferType === 'IMPORT') {
                response = await axiosClient.post('asset-transfer/import', transferData);
            } else {
                response = await axiosClient.post('asset-transfer/export', transferData);
            }

            if (response?.data) {
                showSnackbar(response.data.message || 'Thao tác thành công', 'success');
                handleTransferDialogClose();
                fetchAssets();
            }
        } catch (error) {
            console.error('Error in transfer:', error);
            showSnackbar(error.response?.data?.message || 'Có lỗi xảy ra', 'error');
        }
    };

    const columns = [
        { field: 'code', headerName: 'Mã tài sản', width: 150 },
        { field: 'name', headerName: 'Tên tài sản', width: 200 },
        { field: 'quantity', headerName: 'Số lượng', width: 100 },
        { 
            field: 'unit', 
            headerName: 'Đơn vị', 
            width: 150,
            renderCell: (params) => {
                if (!params.row) return '';
                return params.row.unit?.label || '';
            }
        },
        { field: 'modelOrSeries', headerName: 'Model/Series', width: 150 },
        { 
            field: 'assetType', 
            headerName: 'Loại tài sản', 
            width: 150,
            renderCell: (params) => {
                if (!params.row) return '';
                return params.row.assetType?.label || '';
            }
        },
        { 
            field: 'dateOfPurchase', 
            headerName: 'Ngày mua', 
            width: 150,
            renderCell: (params) => {
                if (!params.row || !params.row.dateOfPurchase) return '';
                return formatDate(params.row.dateOfPurchase);
            }
        },
        { 
            field: 'price', 
            headerName: 'Giá', 
            width: 150,
            renderCell: (params) => {
                if (!params.row || !params.row.price) return '';
                return formatPrice(params.row.price);
            }
        },
        { 
            field: 'managementOffice', 
            headerName: 'Văn phòng quản lý', 
            width: 200,
            renderCell: (params) => {
                if (!params.row) return '';
                return params.row.managementOffice?.label || '';
            }
        },
        {
            field: 'actions',
            headerName: 'Thao tác',
            width: 150,
            renderCell: (params) => {
                if (!params.row) return null;
                return (
                    <Box>
                        <Tooltip title="Chỉnh sửa">
                            <IconButton onClick={() => handleOpenDialog(params.row)} color="primary">
                                <EditIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Xóa">
                            <IconButton onClick={() => handleDelete(params.row._id)} color="error">
                                <DeleteIcon />
                            </IconButton>
                        </Tooltip>
                        <Tooltip title="Điều chuyển">
                            <IconButton onClick={() => handleTransferDialogOpen(params.row)} color="info">
                                <TransferIcon />
                            </IconButton>
                        </Tooltip>
                    </Box>
                );
            }
        }
    ];

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý tài sản</Typography>
                <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => handleOpenDialog()}
                >
                    Thêm mới
                </Button>
            </Box>

            <Paper sx={{ mb: 2 }}>
                <DataTable
                    columns={columns}
                    data={assets}
                    page={pagination.page}
                    rowsPerPage={pagination.limit}
                    total={pagination.total}
                    onPageChange={handlePageChange}
                    onRowsPerPageChange={handleLimitChange}
                />
            </Paper>

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                <DialogTitle>
                    {selectedAsset ? 'Cập nhật tài sản' : 'Thêm mới tài sản'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <Grid container spacing={2}>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Mã tài sản"
                                    name="code"
                                    value={formData.code}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Tên tài sản"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Số lượng"
                                    name="quantity"
                                    type="number"
                                    value={formData.quantity}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    select
                                    label="Đơn vị"
                                    name="unit"
                                    value={formData.unit}
                                    onChange={handleInputChange}
                                    fullWidth
                                >
                                    {units.map((unit) => (
                                        <MenuItem key={unit._id} value={unit._id}>
                                            {unit.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Model/Series"
                                    name="modelOrSeries"
                                    value={formData.modelOrSeries}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    select
                                    label="Loại tài sản"
                                    name="assetType"
                                    value={formData.assetType}
                                    onChange={handleInputChange}
                                    fullWidth
                                >
                                    {assetTypes.map((type) => (
                                        <MenuItem key={type._id} value={type._id}>
                                            {type.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Ngày mua"
                                    name="dateOfPurchase"
                                    type="date"
                                    value={formData.dateOfPurchase}
                                    onChange={handleInputChange}
                                    fullWidth
                                    InputLabelProps={{ shrink: true }}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Bảo hành (tháng)"
                                    name="warranty"
                                    type="number"
                                    value={formData.warranty}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Ngày hết hạn"
                                    name="expirationDate"
                                    type="date"
                                    value={formData.expirationDate}
                                    onChange={handleInputChange}
                                    fullWidth
                                    InputLabelProps={{ shrink: true }}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Giá"
                                    name="price"
                                    type="number"
                                    value={formData.price}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Khấu hao"
                                    name="depreciation"
                                    value={formData.depreciation}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Nhà cung cấp"
                                    name="supplier"
                                    value={formData.supplier}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Địa chỉ NCC"
                                    name="addressNCC"
                                    value={formData.addressNCC}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    label="Số điện thoại NCC"
                                    name="phoneNCC"
                                    value={formData.phoneNCC}
                                    onChange={handleInputChange}
                                    fullWidth
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    select
                                    label="Vị trí tài sản"
                                    name="assetLocation"
                                    value={formData.assetLocation}
                                    onChange={handleInputChange}
                                    fullWidth
                                >
                                    {offices.map((office) => (
                                        <MenuItem key={office._id} value={office._id}>
                                            {office.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    select
                                    label="Văn phòng quản lý"
                                    name="managementOffice"
                                    value={formData.managementOffice}
                                    onChange={handleInputChange}
                                    fullWidth
                                >
                                    {offices.map((office) => (
                                        <MenuItem key={office._id} value={office._id}>
                                            {office.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    label="Mô tả"
                                    name="description"
                                    value={formData.description}
                                    onChange={handleInputChange}
                                    fullWidth
                                    multiline
                                    rows={3}
                                />
                            </Grid>
                        </Grid>
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Hủy</Button>
                    <Button onClick={handleSubmit} variant="contained">
                        {selectedAsset ? 'Cập nhật' : 'Thêm mới'}
                    </Button>
                </DialogActions>
            </Dialog>

            <Dialog open={openTransferDialog} onClose={handleTransferDialogClose} maxWidth="sm" fullWidth>
                <DialogTitle>Điều chuyển tài sản</DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            select
                            label="Loại điều chuyển"
                            name="transferType"
                            value={transferData.transferType}
                            onChange={handleTransferChange}
                            fullWidth
                        >
                            <MenuItem value="IMPORT">Nhập kho</MenuItem>
                            <MenuItem value="EXPORT">Xuất kho</MenuItem>
                        </TextField>
                        <TextField
                            label="Số lượng"
                            name="quantity"
                            type="number"
                            value={transferData.quantity}
                            onChange={handleTransferChange}
                            fullWidth
                        />
                        {transferData.transferType === 'EXPORT' && (
                            <>
                                <TextField
                                    select
                                    label="Từ văn phòng"
                                    name="fromOfficeId"
                                    value={transferData.fromOfficeId}
                                    onChange={handleTransferChange}
                                    fullWidth
                                >
                                    {offices.map((office) => (
                                        <MenuItem key={office._id} value={office._id}>
                                            {office.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                                <TextField
                                    select
                                    label="Đến văn phòng"
                                    name="toOfficeId"
                                    value={transferData.toOfficeId}
                                    onChange={handleTransferChange}
                                    fullWidth
                                >
                                    {offices.map((office) => (
                                        <MenuItem key={office._id} value={office._id}>
                                            {office.label}
                                        </MenuItem>
                                    ))}
                                </TextField>
                            </>
                        )}
                        <TextField
                            label="Ghi chú"
                            name="note"
                            value={transferData.note}
                            onChange={handleTransferChange}
                            fullWidth
                            multiline
                            rows={3}
                        />
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleTransferDialogClose}>Hủy</Button>
                    <Button onClick={handleTransferSubmit} variant="contained">
                        Xác nhận
                    </Button>
                </DialogActions>
            </Dialog>

            <Snackbar
                open={snackbar.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert
                    onClose={handleCloseSnackbar}
                    severity={snackbar.severity}
                    sx={{ width: '100%' }}
                >
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default AssetManagement;
