import React, { useState, useEffect } from 'react';
import {
    Box,
    Typography,
    Paper,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Checkbox,
    IconButton,
    Tooltip,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TablePagination,
    CircularProgress,
    Snackbar,
    Alert,
    Chip,
    Grid,
    TextField,
    MenuItem,
    FormControl,
    InputLabel,
    Select,
    styled
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import FilterListIcon from '@mui/icons-material/FilterList';
import axios from 'axios';
import { tableStyles } from '../styles/TableStyles';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
    padding: theme.spacing(2),
    '&.MuiTableCell-head': {
        backgroundColor: theme.palette.primary.dark,
        color: theme.palette.common.white,
        fontWeight: 'bold',
        fontSize: '0.95rem',
    },
    '&.MuiTableCell-body': {
        fontSize: '0.875rem',
        color: theme.palette.text.primary,
    },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
    '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.grey[50],
    },
    '&:last-child td, &:last-child th': {
        border: 0,
    },
    '&:hover': {
        backgroundColor: theme.palette.primary.light + '10',
    },
}));

const StatusChip = styled(Chip)(({ theme, status }) => ({
    backgroundColor: 
        status === 'Chờ xử lý' ? theme.palette.warning.light + '30' :
        status === 'Hoàn thành' ? theme.palette.success.light + '30' :
        status === 'Đã hủy' ? theme.palette.error.light + '30' :
        theme.palette.grey[100],
    color: 
        status === 'Chờ xử lý' ? theme.palette.warning.dark :
        status === 'Hoàn thành' ? theme.palette.success.dark :
        status === 'Đã hủy' ? theme.palette.error.dark :
        theme.palette.grey[700],
    fontWeight: 500,
}));

const AssetTransferHistory = () => {
    const [logs, setLogs] = useState([]);
    const [loading, setLoading] = useState(false);
    const [selectedLogs, setSelectedLogs] = useState([]);
    const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
    const [pagination, setPagination] = useState({
        page: 0,
        limit: 10,
        total: 0
    });
    const [filters, setFilters] = useState({
        transferType: '',
        status: '',
        fromDate: '',
        toDate: ''
    });
    const [snackbar, setSnackbar] = useState({
        open: false,
        message: '',
        severity: 'success'
    });

    const showSnackbar = (message, severity = 'success') => {
        setSnackbar({
            open: true,
            message,
            severity
        });
    };

    const handleCloseSnackbar = () => {
        setSnackbar(prev => ({ ...prev, open: false }));
    };

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Không tìm thấy token trong localStorage');
            return {};
        }
        return {
            headers: {
                'Authorization': token
            }
        };
    };

    const fetchLogs = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            if (!token) {
                showSnackbar('Vui lòng đăng nhập lại', 'error');
                return;
            }

            const queryParams = new URLSearchParams({
                page: pagination.page + 1,
                limit: pagination.limit,
                ...(filters.transferType && { transferType: filters.transferType }),
                ...(filters.status && { status: filters.status }),
                ...(filters.fromDate && { fromDate: filters.fromDate }),
                ...(filters.toDate && { toDate: filters.toDate })
            });

            const response = await axios.get(
                `http://localhost:3076/api/vanminh/asset-transfer/logs?${queryParams}`,
                getAuthConfig()
            );

            console.log('API Response:', response.data);

            const logs = response.data?.logs || [];

            const formattedLogs = logs.map(log => ({
                ...log,
                assetName: log.assetId?.name || 'Không có tên',
                assetCode: log.assetId?.code || 'Không có mã',
                fromOffice: log.fromOffice?.label || 'Không có thông tin',
                toOffice: log.toOffice?.label || 'Không có thông tin',
                transferBy: log.transferBy?.name || 'Không có thông tin',
                transferType: log.transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho',
                status: log.status === 'PENDING' ? 'Chờ xử lý' : 
                       log.status === 'COMPLETED' ? 'Hoàn thành' :
                       log.status === 'CANCELLED' ? 'Đã hủy' : log.status,
                reason: log.reason === 'NEW_IMPORT' ? 'Nhập mới' :
                       log.reason === 'TRANSFER' ? 'Điều chuyển' :
                       log.reason === 'RETURN' ? 'Trả về' : 'Bảo trì',
                createdAt: log.transferDate ? new Date(log.transferDate).toLocaleDateString('vi-VN') : ''
            }));

            console.log('Formatted Logs:', formattedLogs);

            setLogs(formattedLogs);
            setPagination(prev => ({
                ...prev,
                total: response.data?.total || 0
            }));
        } catch (error) {
            console.error('Lỗi khi tải logs:', error);
            if (error.response?.status === 403) {
                showSnackbar('Phiên làm việc đã hết hạn, vui lòng đăng nhập lại', 'error');
            } else {
                showSnackbar('Lỗi khi tải dữ liệu', 'error');
            }
            setLogs([]);
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteLogs = async () => {
        try {
            await axios.delete(
                'http://localhost:3076/api/vanminh/asset-transfer/logs',
                { 
                    ...getAuthConfig(),
                    data: { ids: selectedLogs }
                }
            );

            showSnackbar('Xóa thành công', 'success');
            setSelectedLogs([]);
            setOpenDeleteDialog(false);
            fetchLogs();
        } catch (error) {
            console.error('Lỗi khi xóa logs:', error);
            showSnackbar('Lỗi khi xóa dữ liệu', 'error');
        }
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleApplyFilters = () => {
        setPagination(prev => ({ ...prev, page: 0 }));
        fetchLogs();
    };

    const handleResetFilters = () => {
        setFilters({
            transferType: '',
            status: '',
            fromDate: '',
            toDate: ''
        });
        setPagination(prev => ({ ...prev, page: 0 }));
        fetchLogs();
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'Chờ xử lý':
                return 'warning';
            case 'Hoàn thành':
                return 'success';
            case 'Đã hủy':
                return 'error';
            default:
                return 'default';
        }
    };

    const columns = [
        { 
            id: 'checkbox',
            label: <Checkbox
                indeterminate={selectedLogs.length > 0 && selectedLogs.length < logs.length}
                checked={logs.length > 0 && selectedLogs.length === logs.length}
                onChange={(e) => {
                    if (e.target.checked) {
                        setSelectedLogs(logs.map(log => log._id));
                    } else {
                        setSelectedLogs([]);
                    }
                }}
            />,
            width: '5%'
        },
        { id: 'assetName', label: 'Tên tài sản', width: '20%' },
        { id: 'transferType', label: 'Loại', width: '10%' },
        { id: 'quantity', label: 'Số lượng', width: '10%' },
        { id: 'fromOffice', label: 'Từ văn phòng', width: '15%' },
        { id: 'toOffice', label: 'Đến văn phòng', width: '15%' },
        { id: 'transferBy', label: 'Người thực hiện', width: '15%' },
        { id: 'status', label: 'Trạng thái', width: '10%' },
        { id: 'createdAt', label: 'Ngày tạo', width: '10%' }
    ];

    const formatDateTime = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return date.toLocaleString('vi-VN', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    };

    useEffect(() => {
        fetchLogs();
    }, [pagination.page, pagination.limit]);

    return (
        <Box sx={tableStyles.pageContainer}>
            <Box sx={tableStyles.headerContainer}>
                <Typography variant="h4">Lịch sử điều chuyển tài sản</Typography>
                {selectedLogs.length > 0 && (
                    <Button
                        variant="contained"
                        color="error"
                        startIcon={<DeleteIcon />}
                        onClick={() => setOpenDeleteDialog(true)}
                    >
                        Xóa ({selectedLogs.length})
                    </Button>
                )}
            </Box>

            <Paper sx={tableStyles.paper}>
                <Grid container spacing={2} alignItems="center" sx={{ p: 2 }}>
                    <Grid item xs={12} sm={6} md={3}>
                        <FormControl fullWidth>
                            <InputLabel>Loại điều chuyển</InputLabel>
                            <Select
                                name="transferType"
                                value={filters.transferType}
                                onChange={handleFilterChange}
                                label="Loại điều chuyển"
                            >
                                <MenuItem value="">Tất cả</MenuItem>
                                <MenuItem value="IMPORT">Nhập kho</MenuItem>
                                <MenuItem value="EXPORT">Xuất kho</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <FormControl fullWidth>
                            <InputLabel>Trạng thái</InputLabel>
                            <Select
                                name="status"
                                value={filters.status}
                                onChange={handleFilterChange}
                                label="Trạng thái"
                            >
                                <MenuItem value="">Tất cả</MenuItem>
                                <MenuItem value="PENDING">Chờ xử lý</MenuItem>
                                <MenuItem value="COMPLETED">Hoàn thành</MenuItem>
                                <MenuItem value="CANCELLED">Đã hủy</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <TextField
                            fullWidth
                            type="datetime-local"
                            name="fromDate"
                            label="Từ ngày"
                            value={filters.fromDate}
                            onChange={handleFilterChange}
                            InputLabelProps={{ shrink: true }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <TextField
                            fullWidth
                            type="datetime-local"
                            name="toDate"
                            label="Đến ngày"
                            value={filters.toDate}
                            onChange={handleFilterChange}
                            InputLabelProps={{ shrink: true }}
                        />
                    </Grid>
                    <Grid item xs={12} sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                        <Button
                            variant="outlined"
                            onClick={handleResetFilters}
                        >
                            Đặt lại
                        </Button>
                        <Button
                            variant="contained"
                            startIcon={<FilterListIcon />}
                            onClick={handleApplyFilters}
                        >
                            Áp dụng
                        </Button>
                    </Grid>
                </Grid>

                <TableContainer sx={tableStyles.tableContainer}>
                    {loading ? (
                        <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
                            <CircularProgress />
                        </Box>
                    ) : (
                        <Table stickyHeader>
                            <TableHead>
                                <TableRow>
                                    {columns.map(column => (
                                        <StyledTableCell
                                            key={column.id}
                                            style={{ width: column.width }}
                                        >
                                            {column.label}
                                        </StyledTableCell>
                                    ))}
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {logs.map(log => (
                                    <StyledTableRow key={log._id}>
                                        <StyledTableCell>
                                            <Checkbox
                                                checked={selectedLogs.indexOf(log._id) !== -1}
                                                onChange={(e) => {
                                                    if (e.target.checked) {
                                                        setSelectedLogs([...selectedLogs, log._id]);
                                                    } else {
                                                        setSelectedLogs(selectedLogs.filter(id => id !== log._id));
                                                    }
                                                }}
                                            />
                                        </StyledTableCell>
                                        <StyledTableCell>
                                            <Tooltip title={`${log.assetName} (${log.assetCode})`}>
                                                <Box sx={tableStyles.cellContent}>
                                                    {log.assetName} ({log.assetCode})
                                                </Box>
                                            </Tooltip>
                                        </StyledTableCell>
                                        <StyledTableCell>{log.transferType}</StyledTableCell>
                                        <StyledTableCell>{log.quantity}</StyledTableCell>
                                        <StyledTableCell>{log.fromOffice}</StyledTableCell>
                                        <StyledTableCell>{log.toOffice}</StyledTableCell>
                                        <StyledTableCell>{log.transferBy}</StyledTableCell>
                                        <StyledTableCell>
                                            <StatusChip
                                                label={log.status}
                                                status={log.status}
                                                size="small"
                                            />
                                        </StyledTableCell>
                                        <StyledTableCell>{formatDateTime(log.createdAt)}</StyledTableCell>
                                    </StyledTableRow>
                                ))}
                            </TableBody>
                        </Table>
                    )}
                </TableContainer>
                <TablePagination
                    component="div"
                    count={pagination.total}
                    page={pagination.page}
                    onPageChange={(e, newPage) => setPagination(prev => ({ ...prev, page: newPage }))}
                    rowsPerPage={pagination.limit}
                    onRowsPerPageChange={(e) => setPagination(prev => ({ ...prev, limit: parseInt(e.target.value, 10), page: 0 }))}
                    rowsPerPageOptions={[5, 10, 25, 50]}
                    labelRowsPerPage="Số hàng mỗi trang:"
                    labelDisplayedRows={({ from, to, count }) => 
                        `${from}-${to} của ${count !== -1 ? count : `hơn ${to}`}`}
                />
            </Paper>

            <Dialog
                open={openDeleteDialog}
                onClose={() => setOpenDeleteDialog(false)}
            >
                <DialogTitle>Xác nhận xóa</DialogTitle>
                <DialogContent>
                    Bạn có chắc chắn muốn xóa {selectedLogs.length} bản ghi đã chọn?
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpenDeleteDialog(false)}>Hủy</Button>
                    <Button onClick={handleDeleteLogs} color="error">Xóa</Button>
                </DialogActions>
            </Dialog>

            <Snackbar
                open={snackbar.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert
                    onClose={handleCloseSnackbar}
                    severity={snackbar.severity}
                    sx={{ width: '100%' }}
                >
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default AssetTransferHistory;
