import React from 'react';
import { Box } from '@mui/material';
import { Outlet } from 'react-router-dom';
import Sidebar from './Sidebar';

const Layout = () => {
    return (
        <Box sx={{ display: 'flex' }}>
            <Sidebar />
            <Box 
                component="main" 
                sx={{ 
                    flexGrow: 1,
                    p: 3,
                    ml: '240px', // Margin left bằng với chiều rộng của sidebar
                    minHeight: '100vh',
                    backgroundColor: '#f5f5f5', // Màu nền nhẹ
                    '& > *': {
                        backgroundColor: '#fff', // Màu nền trắng cho các component con
                        borderRadius: 2, // Bo góc nhẹ
                        boxShadow: '0 0 10px rgba(0,0,0,0.1)', // Đổ bóng nhẹ
                    }
                }}
            >
                <Outlet />
            </Box>
        </Box>
    );
};

export default Layout;
