import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Layout from './components/Layout';
import { SnackbarProvider } from 'notistack';

// Tạo theme mới với màu vàng nhẹ nhàng
const theme = createTheme({
  palette: {
    primary: {
      main: '#FFD54F', // Màu vàng nhạt
      light: '#FFE082',
      dark: '#FFB300',
    },
    secondary: {
      main: '#FFB74D', // Màu cam nhạt
      light: '#FFCC80',
      dark: '#FF9800',
    },
    background: {
      default: '#FFFDE7', // Màu nền vàng rất nhạt
      paper: '#FFFFFF',
    },
    text: {
      primary: '#5D4037', // Màu nâu nhạt
      secondary: '#8D6E63',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontSize: '2.5rem',
      fontWeight: 400,
      color: '#5D4037',
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 400,
      color: '#5D4037',
    },
    h3: {
      fontSize: '1.75rem',
      fontWeight: 400,
      color: '#5D4037',
    },
    h4: {
      fontSize: '1.5rem',
      fontWeight: 400,
      color: '#5D4037',
    },
    h5: {
      fontSize: '1.25rem',
      fontWeight: 400,
      color: '#5D4037',
    },
    h6: {
      fontSize: '1rem',
      fontWeight: 400,
      color: '#5D4037',
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: 'none',
          fontWeight: 400,
          padding: '8px 16px',
        },
        contained: {
          boxShadow: 'none',
          '&:hover': {
            boxShadow: 'none',
          },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)',
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
          borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
        },
      },
    },
    MuiDrawer: {
      styleOverrides: {
        paper: {
          borderRight: 'none',
          boxShadow: '2px 0 8px rgba(0, 0, 0, 0.05)',
        },
      },
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <SnackbarProvider
        maxSnack={3}
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        autoHideDuration={3000}
      >
        <Router>
          <Layout>
            <Routes>
              <Route path="/" element={<div>Trang chủ</div>} />
              <Route path="/users" element={<div>Quản lý nhân sự</div>} />
              <Route path="/departments" element={<div>Quản lý phòng ban</div>} />
              <Route path="/cars" element={<div>Quản lý xe</div>} />
              <Route path="/assets" element={<div>Quản lý tài sản</div>} />
            </Routes>
          </Layout>
        </Router>
      </SnackbarProvider>
    </ThemeProvider>
  );
}

export default App;
