import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Typography,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';
import { Add as AddIcon } from '@mui/icons-material';
import axios from 'axios';
import DataTable from '../components/DataTable';

const CarManagement = () => {
    const [cars, setCars] = useState([]);
    const [teams, setTeams] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [selectedCar, setSelectedCar] = useState(null);
    const [formData, setFormData] = useState({
        carType: '',
        bks: '',
        team: '',
        yearOfManufacture: '',
        registrationPeriod: '',
        registrationName: '',
        valuation: '',
        insurancePeriodTNDS: '',
        insuranceSellerTNDS: '',
        insurancePeriodBHVC: '',
        insuranceSellerBHVC: '',
        insurancePeriodGPS: '',
        descriptionGPS: '',
        insurancePeriod4G: '',
        insuranceSeller4G: '',
        description: '',
    });

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        return {
            headers: {
                'Authorization': token
            }
        };
    };

    useEffect(() => {
        fetchCars();
        fetchTeams();
    }, []);

    const fetchCars = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/car', getAuthConfig());
            setCars(response.data.data || []);
        } catch (error) {
            console.error('Error fetching cars:', error);
        }
    };

    const fetchTeams = async () => {
        try {
            const response = await axios.get('http://localhost:3076/api/vanminh/team', getAuthConfig());
            setTeams(response.data || []);
        } catch (error) {
            console.error('Error fetching teams:', error);
        }
    };

    const handleOpenDialog = (car = null) => {
        if (car) {
            setSelectedCar(car);
            setFormData({
                carType: car.carType || '',
                bks: car.bks || '',
                team: car.team || '',
                yearOfManufacture: car.yearOfManufacture || '',
                registrationPeriod: car.registrationPeriod || '',
                registrationName: car.registrationName || '',
                valuation: car.valuation || '',
                insurancePeriodTNDS: car.insurancePeriodTNDS || '',
                insuranceSellerTNDS: car.insuranceSellerTNDS || '',
                insurancePeriodBHVC: car.insurancePeriodBHVC || '',
                insuranceSellerBHVC: car.insuranceSellerBHVC || '',
                insurancePeriodGPS: car.insurancePeriodGPS || '',
                descriptionGPS: car.descriptionGPS || '',
                insurancePeriod4G: car.insurancePeriod4G || '',
                insuranceSeller4G: car.insuranceSeller4G || '',
                description: car.description || '',
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setSelectedCar(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async () => {
        try {
            if (selectedCar) {
                await axios.put(
                    `http://localhost:3076/api/vanminh/car/${selectedCar._id}`, 
                    formData,
                    getAuthConfig()
                );
            } else {
                await axios.post(
                    'http://localhost:3076/api/vanminh/car', 
                    formData,
                    getAuthConfig()
                );
            }
            handleCloseDialog();
            fetchCars();
        } catch (error) {
            console.error('Error saving car:', error);
        }
    };

    const handleDelete = async (car) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa?')) {
            try {
                await axios.delete(
                    `http://localhost:3076/api/vanminh/car/${car._id}`,
                    getAuthConfig()
                );
                fetchCars();
            } catch (error) {
                console.error('Error deleting car:', error);
            }
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return date.toLocaleDateString('vi-VN');
    };

    const columns = [
        { field: 'carType', headerName: 'Loại xe' },
        { field: 'bks', headerName: 'Biển số' },
        { 
            field: 'team', 
            headerName: 'Team',
            renderCell: (row) => row.team?.label || 'N/A'
        },
        { 
            field: 'yearOfManufacture', 
            headerName: 'Năm sản xuất',
            renderCell: (row) => formatDate(row.yearOfManufacture)
        },
        { 
            field: 'registrationPeriod', 
            headerName: 'Thời hạn đăng kiểm',
            renderCell: (row) => formatDate(row.registrationPeriod)
        },
        { field: 'registrationName', headerName: 'Tên đăng ký' },
        { 
            field: 'valuation', 
            headerName: 'Định giá',
            renderCell: (row) => `${row.valuation?.toLocaleString('vi-VN')} VNĐ`
        },
        {
            field: 'insurance',
            headerName: 'Bảo hiểm',
            renderCell: (row) => (
                <div>
                    <div>TNDS: {formatDate(row.insurancePeriodTNDS)}</div>
                    <div>BHVC: {formatDate(row.insurancePeriodBHVC)}</div>
                </div>
            )
        }
    ];

    return (
        <Box sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h4">Quản lý xe</Typography>
                <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => handleOpenDialog()}
                >
                    Thêm mới
                </Button>
            </Box>

            <DataTable
                columns={columns}
                data={cars}
                onEdit={handleOpenDialog}
                onDelete={handleDelete}
            />

            <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
                <DialogTitle>
                    {selectedCar ? 'Cập nhật thông tin xe' : 'Thêm mới xe'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Loại xe"
                            name="carType"
                            value={formData.carType}
                            onChange={handleInputChange}
                            fullWidth
                            required
                        />
                        <TextField
                            label="Biển số"
                            name="bks"
                            value={formData.bks}
                            onChange={handleInputChange}
                            fullWidth
                            required
                        />
                        <FormControl fullWidth>
                            <InputLabel>Team</InputLabel>
                            <Select
                                name="team"
                                value={formData.team}
                                onChange={handleInputChange}
                                label="Team"
                                required
                            >
                                {teams.map((team) => (
                                    <MenuItem key={team._id} value={team._id}>
                                        {team.label}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        <TextField
                            label="Năm sản xuất"
                            name="yearOfManufacture"
                            type="date"
                            value={formData.yearOfManufacture}
                            onChange={handleInputChange}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                        />
                        <TextField
                            label="Thời hạn đăng kiểm"
