import React from "react";
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Box,
  Typography,
  TablePagination,
  TableSortLabel,
  Tooltip,
  useTheme,
  useMediaQuery,
  styled,
  Chip,
  Avatar,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import InfoIcon from "@mui/icons-material/Info";
import WarningIcon from "@mui/icons-material/Warning";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import ErrorIcon from "@mui/icons-material/Error";
import { visuallyHidden } from '@mui/utils';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  padding: theme.spacing(2),
  '&.MuiTableCell-head': {
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.common.white,
    fontWeight: 'bold',
    fontSize: '0.95rem',
    '& .MuiTableSortLabel-root': {
      color: theme.palette.common.white,
      '&:hover': {
        color: theme.palette.primary.light,
      },
      '&.Mui-active': {
        color: theme.palette.primary.light,
      },
    },
  },
  '&.MuiTableCell-body': {
    fontSize: '0.875rem',
    color: theme.palette.text.primary,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.grey[50],
  },
  '&:last-child td, &:last-child th': {
    border: 0,
  },
  '&:hover': {
    backgroundColor: theme.palette.primary.light + '10',
  },
}));

const ActionCell = styled(Box)(({ theme }) => ({
  display: 'flex',
  gap: theme.spacing(1),
  justifyContent: 'flex-end',
}));

const StatusChip = styled(Chip)(({ theme, status }) => ({
  backgroundColor: 
    status === 'active' ? theme.palette.success.light + '30' :
    status === 'inactive' ? theme.palette.error.light + '30' :
    status === 'pending' ? theme.palette.warning.light + '30' :
    theme.palette.grey[100],
  color: 
    status === 'active' ? theme.palette.success.dark :
    status === 'inactive' ? theme.palette.error.dark :
    status === 'pending' ? theme.palette.warning.dark :
    theme.palette.grey[700],
  fontWeight: 500,
  '& .MuiChip-icon': {
    color: 'inherit',
  },
}));

const DataTable = ({ 
  columns, 
  data, 
  onEdit, 
  onDelete,
  page,
  rowsPerPage,
  total,
  onPageChange,
  onRowsPerPageChange
}) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const renderStatusIcon = (status) => {
    switch (status) {
      case 'active':
        return <CheckCircleIcon fontSize="small" />;
      case 'inactive':
        return <ErrorIcon fontSize="small" />;
      case 'pending':
        return <WarningIcon fontSize="small" />;
      default:
        return <InfoIcon fontSize="small" />;
    }
  };

  const renderStatusCell = (row, field) => {
    const status = row[field];
    return (
      <StatusChip
        icon={renderStatusIcon(status)}
        label={status === 'active' ? 'Hoạt động' : 
               status === 'inactive' ? 'Không hoạt động' : 
               status === 'pending' ? 'Đang chờ' : 'Không xác định'}
        status={status}
      />
    );
  };

  const renderActionButtons = (row) => {
    return (
      <ActionCell>
        {onEdit && (
          <Tooltip title="Chỉnh sửa">
            <IconButton 
              onClick={() => onEdit(row)} 
              color="primary"
              size={isMobile ? "small" : "medium"}
              sx={{
                backgroundColor: theme.palette.primary.light + '20',
                '&:hover': {
                  backgroundColor: theme.palette.primary.light + '40',
                },
              }}
            >
              <EditIcon />
            </IconButton>
          </Tooltip>
        )}
        {onDelete && (
          <Tooltip title="Xóa">
            <IconButton 
              onClick={() => onDelete(row._id)} 
              color="error"
              size={isMobile ? "small" : "medium"}
              sx={{
                backgroundColor: theme.palette.error.light + '20',
                '&:hover': {
                  backgroundColor: theme.palette.error.light + '40',
                },
              }}
            >
              <DeleteIcon />
            </IconButton>
          </Tooltip>
        )}
      </ActionCell>
    );
  };

  if (!columns.length || !data.length) {
    return (
      <Paper 
        sx={{ 
          p: 3, 
          textAlign: 'center',
          backgroundColor: theme.palette.grey[50],
          borderRadius: 2,
        }}
      >
        <InfoIcon sx={{ fontSize: 40, color: theme.palette.grey[400], mb: 1 }} />
        <Typography color="text.secondary" variant="h6">
          Không có dữ liệu để hiển thị
        </Typography>
      </Paper>
    );
  }

  return (
    <Box sx={{ 
      width: '100%',
      overflowX: 'auto',
      ml: -0.5,
      '& .MuiTableContainer-root': {
        borderRadius: 0,
        boxShadow: 'none'
      },
      '& .MuiTable-root': {
        borderCollapse: 'separate',
        borderSpacing: 0,
        minWidth: '100%'
      },
      '& .MuiTableHead-root': {
        backgroundColor: '#FFA726',
        '& .MuiTableCell-head': {
          color: 'white',
          fontWeight: 'bold',
          whiteSpace: 'nowrap',
          borderBottom: 'none',
          padding: '12px 16px',
          '&:first-of-type': {
            paddingLeft: '8px'
          }
        }
      },
      '& .MuiTableBody-root': {
        '& .MuiTableRow-root': {
          '&:hover': {
            backgroundColor: 'rgba(0, 0, 0, 0.04)'
          },
          '& .MuiTableCell-body': {
            padding: '12px 16px',
            borderBottom: '1px solid rgba(224, 224, 224, 1)',
            '&:first-of-type': {
              paddingLeft: '8px'
            }
          }
        }
      },
      '& .MuiTablePagination-root': {
        borderTop: '1px solid rgba(224, 224, 224, 1)',
        '& .MuiTablePagination-selectLabel, & .MuiTablePagination-displayedRows': {
          margin: 0
        }
      }
    }}>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map((column, index) => (
                <TableCell key={index}>{column.headerName}</TableCell>
              ))}
              <TableCell align="right" sx={{ pr: 2 }}>Thao tác</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row, rowIndex) => (
              <TableRow key={rowIndex}>
                {columns.map((column, colIndex) => (
                  <TableCell key={colIndex}>
                    {column.field === 'status' 
                      ? renderStatusCell(row, column.field)
                      : column.renderCell 
                        ? column.renderCell(row) 
                        : row[column.field]}
                  </TableCell>
                ))}
                <TableCell align="right" sx={{ pr: 2 }}>
                  {renderActionButtons(row)}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        component="div"
        count={total}
        page={page}
        onPageChange={onPageChange}
        rowsPerPage={rowsPerPage}
        onRowsPerPageChange={onRowsPerPageChange}
        rowsPerPageOptions={[5, 10, 25, 50]}
        labelRowsPerPage="Số dòng mỗi trang:"
        labelDisplayedRows={({ from, to, count }) => 
          `${from}-${to} của ${count !== -1 ? count : `hơn ${to}`}`}
      />
    </Box>
  );
};

export default DataTable;
