import React, { useState, useEffect } from 'react';
import {
    Box,
    Typography,
    MenuItem,
    TextField,
    Grid,
    Chip,
    Paper
} from '@mui/material';
import DataTable from '../components/DataTable';
import axios from 'axios';

const AssetTransferHistory = () => {
    const [logs, setLogs] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [total, setTotal] = useState(0);
    const [filter, setFilter] = useState({
        transferType: '',
        status: ''
    });
    const [loading, setLoading] = useState(false);

    const getAuthConfig = () => {
        const token = localStorage.getItem('token');
        return {
            headers: {
                'Authorization': token
            }
        };
    };

    const fetchLogs = async () => {
        try {
            setLoading(true);
            const response = await axios.get(
                `http://localhost:3076/api/vanminh/asset-transfer/logs`,
                {
                    ...getAuthConfig(),
                    params: {
                        page: page + 1,
                        limit: rowsPerPage,
                        ...filter
                    }
                }
            );

            console.log('Response from API:', response.data);

            if (response.data && response.data.logs) {
                const formattedLogs = response.data.logs.map(log => ({
                    _id: log._id,
                    assetName: log.assetId ? `${log.assetId.name || ''} - ${log.assetId.code || ''}` : '',
                    fromOffice: log.fromOffice ? log.fromOffice.label || '' : '',
                    toOffice: log.toOffice ? log.toOffice.label || '' : '',
                    quantity: log.quantity || 0,
                    transferType: log.transferType,
                    transferTypeText: log.transferType === 'IMPORT' ? 'Nhập kho' : 'Xuất kho',
                    status: log.status,
                    statusText: log.status === 'PENDING' ? 'Đang xử lý' : 
                              log.status === 'COMPLETED' ? 'Hoàn thành' : 'Đã hủy',
                    reason: log.reason,
                    reasonText: log.reason === 'NEW_IMPORT' ? 'Nhập thêm' :
                              log.reason === 'TRANSFER' ? 'Điều chuyển' :
                              log.reason === 'RETURN' ? 'Trả về' : 'Bảo trì',
                    transferBy: log.transferBy ? log.transferBy.name || '' : '',
                    transferDate: log.transferDate ? new Date(log.transferDate).toLocaleString('vi-VN') : '',
                    note: log.note || ''
                }));

                console.log('Formatted logs:', formattedLogs);
                setLogs(formattedLogs);
                setTotal(response.data.total || 0);
            }
        } catch (error) {
            console.error('Error fetching transfer logs:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchLogs();
    }, [page, rowsPerPage, filter]);

    const handleFilterChange = (event) => {
        const { name, value } = event.target;
        setFilter(prev => ({
            ...prev,
            [name]: value
        }));
        setPage(0);
    };

    const columns = [
        {
            field: 'assetName',
            headerName: 'Tài sản',
            width: 200,
            renderCell: (row) => row.assetName || ''
        },
        {
            field: 'transferType',
            headerName: 'Loại',
            width: 120,
            renderCell: (row) => (
                <Chip
                    label={row.transferTypeText || ''}
                    color={row.transferType === 'IMPORT' ? 'success' : 'info'}
                    size="small"
                />
            )
        },
        {
            field: 'quantity',
            headerName: 'Số lượng',
            width: 100,
            renderCell: (row) => row.quantity || 0
        },
        {
            field: 'fromOffice',
            headerName: 'Từ văn phòng',
            width: 150,
            renderCell: (row) => row.fromOffice || ''
        },
        {
            field: 'toOffice',
            headerName: 'Đến văn phòng',
            width: 150,
            renderCell: (row) => row.toOffice || ''
        },
        {
            field: 'status',
            headerName: 'Trạng thái',
            width: 130,
            renderCell: (row) => (
                <Chip
                    label={row.statusText || ''}
                    color={
                        row.status === 'COMPLETED' ? 'success' :
                        row.status === 'PENDING' ? 'warning' : 'error'
                    }
                    size="small"
                />
            )
        },
        {
            field: 'reasonText',
            headerName: 'Lý do',
            width: 150,
            renderCell: (row) => row.reasonText || ''
        },
        {
            field: 'transferBy',
            headerName: 'Người thực hiện',
            width: 150,
            renderCell: (row) => row.transferBy || ''
        },
        {
            field: 'transferDate',
            headerName: 'Ngày thực hiện',
            width: 180,
            renderCell: (row) => row.transferDate || ''
        },
        {
            field: 'note',
            headerName: 'Ghi chú',
            width: 200,
            renderCell: (row) => row.note || ''
        }
    ];

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h5" sx={{ mb: 3 }}>
                Lịch sử điều chuyển kho
            </Typography>

            <Paper sx={{ p: 2, mb: 3 }}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            select
                            label="Loại điều chuyển"
                            name="transferType"
                            value={filter.transferType}
                            onChange={handleFilterChange}
                        >
                            <MenuItem value="">Tất cả</MenuItem>
                            <MenuItem value="IMPORT">Nhập kho</MenuItem>
                            <MenuItem value="EXPORT">Xuất kho</MenuItem>
                        </TextField>
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            select
                            label="Trạng thái"
                            name="status"
                            value={filter.status}
                            onChange={handleFilterChange}
                        >
                            <MenuItem value="">Tất cả</MenuItem>
                            <MenuItem value="PENDING">Đang xử lý</MenuItem>
                            <MenuItem value="COMPLETED">Hoàn thành</MenuItem>
                            <MenuItem value="CANCELLED">Đã hủy</MenuItem>
                        </TextField>
                    </Grid>
                </Grid>
            </Paper>

            <DataTable
                columns={columns}
                data={logs}
                page={page}
                rowsPerPage={rowsPerPage}
                total={total}
                loading={loading}
                onPageChange={(event, newPage) => setPage(newPage)}
                onRowsPerPageChange={(event) => {
                    setRowsPerPage(parseInt(event.target.value, 10));
                    setPage(0);
                }}
            />
        </Box>
    );
};

export default AssetTransferHistory;
